public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    dirPartyLookupEntitiesFilter dirPartyLookupEntitiesFilter;

    dirPartyLookupEntitiesFilter = dirPartyLookupEntitiesFilter::generateDirPartyLookupEntitiesFilter(
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::All ? NoYes::Yes : NoYes::No),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::CustomersOnly ? FilterStatus::Set : FilterStatus::Undefined),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::VendorsOnly ? FilterStatus::Set : FilterStatus::Undefined),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::BusinessRelationsOnly ? FilterStatus::Set : FilterStatus::Undefined),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::CompetitorsOnly ? FilterStatus::Set : FilterStatus::Undefined),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::ContactsOnly ? FilterStatus::Set : FilterStatus::Undefined),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::EmployeesOnly ? FilterStatus::Set : FilterStatus::Undefined),
                                                                    (auditPolicyListParty.AddressBookRole == DirPartyRoleView::ApplicantsOnly ? FilterStatus::Set : FilterStatus::Undefined)
                                                                    );

    return DirUtilityEx::namelookup(_formReferenceControl, auditPolicyListParty, DirPartyType::None, element, false, dirPartyLookupEntitiesFilter);
}

public void lookup()
{
    tmpSysTableField.DictTableId = tableName2id(auditPolicyRuleDetail.SourceRelationType);
    tableFieldLookup.performLookupTable(this, tmpSysTableField);
}

public void lookup()
{
    Set restrictedTypes = new Set(Types::Enum);

    restrictedTypes.add(enum2int(Types::String));

    tmpSysTableField.DictTableId = tableName2id(auditPolicyRuleDetail.SourceRelationType);

    tableFieldLookup.initDataSource(tmpSysTableField);
    tableFieldLookup.parmRestrictedTypes(restrictedTypes);
    tableFieldLookup.performLookupField(this, tmpSysTableField);
}

void clicked()
{
    Args                            args;
    Formrun                         formrun;
    SysPolicySourceDocumentRule     auditRuleCopy;

    super();

    auditRule_ds.write();

    // Make a copy of the rule to test
    auditRuleCopy.data(auditRule);
    auditRuleCopy.QueryPacked = auditPolicyRuleRunBase.queryRun().query().pack();

    args = new Args();
    args.caller(element);
    args.record(auditRuleCopy);
    args.name(formstr(AuditPolicyDateSelection));
    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait(true);
}

void clicked()
{
    auditRule_ds.write();       // ensure the SysQueryForm query details gets saved with the policy rule record

    // ensure required supporting data exists before allowing the form to close
    if (element.validateSupportingData())
    {
        element.close();
    }
}

void clicked()
{
    boolean canCancel;

    if (editMode)
    {
        // user is editing an existing policy rule record
        // verify required supporting data exists before allowing the form to close
        canCancel = element.validateSupportingData();
    }
    else
    {
        // user canceled the action of adding the new policy rule so delete it

        ttsbegin;

        // set ValidFrom to a future date to allow the record to be deleted
        // otherwise the logic in the SysPolicyRule::delete() method will cause the record to be retained
        auditRule.ValidFrom = DateTimeUtil::maxValue();
        auditRule.delete();

        ttscommit;

        canCancel = true;
    }

    if (canCancel)
    {
        super();
    }
}
