    Object                  formServiceOrderTable;
    SMAServiceOrderLine     serviceOrderLine;
    BOMId                   parentBOMId;
    RouteId                 parentRouteId;
    BOMDesignerBOMCalc      bomDesignerBOMCalc;
    [FormObservable]
    SalesUnit               salesUnit;
    BOMCalcTable            bomCalcTableFromLookup;

    InventDimCtrl_Frm       inventDimFormSetup;

    PdsComdPricingMarginLine    comdPricingMarginLine;
