    return bomDesignerBOMCalc;
}

CostingVersion costingVersion()
{
    return null;
}

void init()
{
    if (element.args())
    {
        if (element.args().dataset() == tablenum(SalesLine))
        {
            formSalesTable      = element.args().caller();
            salesLine           = element.args().record();
            salesUnit           = salesLine.SalesUnit;
        }
        else if (element.args().dataset() == tablenum(SalesQuotationLine))
        {
            formSalesQuotationTable      = element.args().caller();
            salesQuotationLine           = element.args().record();
            salesUnit                    = salesQuotationLine.SalesUnit;
        }
        else if (classidget(element.args().caller()) == classnum(BOMDesignerBOMCalc))
        {
            bomDesignerBOMCalc = element.args().caller() as BOMDesignerBOMCalc;
        }
        else if (element.args().dataset() == tablenum(SMAServiceOrderLine))
        {
            formServiceOrderTable   = element.args().caller();
            serviceOrderLine        = element.args().record();
            salesUnit               = serviceOrderLine.Unit;
        }
        else if (element.args().lookupRecord() && element.args().lookupRecord().TableId == tableNum(BOMCalcTable))
        {
            bomCalcTableFromLookup = element.args().lookupRecord();
        }
    }
    pmfFormCtrl_BOMCalcTable = PmfFormCtrl_BOMCalcTable::newFromForm(element);
    super();

    if ((formSalesTable && salesLine)
        || (formSalesQuotationTable && salesQuotationLine)
        || (formServiceOrderTable && serviceOrderLine))
    {
        salesOrderUnit.visible(true);
    }
    else
    {
        salesOrderUnit.visible(false);
    }
    element.updateDesign(InventDimFormDesignUpdate::Init);

    pmfFormCtrl_BOMCalcTable.setDatasources(bomCalcTable);
    pmfFormCtrl_BOMCalcTable.initPost();
}

