void run()
{
    transferToSales.enabled(false);
    transferToSalesQuotation.enabled(false);
    transferToServiceOrder.enabled(false);

    super();

    AccessType bomCalcAccess = SysDictMenu::newMenuItem(menuItemActionStr(BOMCalc),MenuItemType::Action).rights();

    if ((salesQuotationLine || salesLine || bomDesignerBOMCalc || serviceOrderLine) && (isConfigurationkeyEnabled(configurationKeyNum(BOM)) && bomCalcAccess > AccessType::Add))
    {
        ctrlCalculate.clicked();
    }
}

