void transferToSalesLine()
{
    Dialog              dialog = new Dialog("@SYS26376");
    DialogField         dialogSalesPrice;
    DialogField         dialogQty;

    DialogField         dialogPriceInvent;
    DialogField         dialogPriceSales;
    DialogField         dialogQtyInvent;
    DialogField         dialogQtySales;

    DialogField         dialogBOMIdMove;
    DialogField         dialogRouteIdMove;
    DialogField         dialogBOMId;
    DialogField         dialogRouteId;

    boolean             bomVersionsEnabled   = isConfigurationkeyEnabled(configurationkeynum(BOMVersion));
    boolean             routeEnabled         = isConfigurationkeyEnabled(configurationkeynum(ProdRouting));

    InventTableModule   inventTableModule;

    Qty                 qtyInvent;
    Qty                 qtySales;
    Amount              priceInvent;
    Amount              priceSales;

    inventTableModule   = InventTableModule::find(bomCalcTable.ItemId,ModuleInventPurchSales::Sales);
    qtyInvent           = bomCalcTable.Qty;
    qtySales            = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(bomCalcTable.ItemId,
                                                                                                bomCalcTable.InventDimId,
                                                                                                qtyInvent,
                                                                                                bomCalcTable.UnitId,
                                                                                                salesLine.SalesUnit,
                                                                                                NoYes::Yes);

    priceInvent         = CurrencyExchangeHelper::price(bomCalcTable.salesPricePcs());
    priceSales          = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(bomCalcTable.ItemId,
                                                                                                bomCalcTable.InventDimId,
                                                                                                bomCalcTable.salesPricePcs(),
                                                                                                salesLine.SalesUnit,
                                                                                                bomCalcTable.UnitId,
                                                                                                NoYes::Yes,         
                                                                                                NoYes::No);

    dialogSalesPrice = dialog.addField(enumstr(NoYes));
    dialogSalesPrice.label("@SYS26377");
    dialogSalesPrice.value(NoYes::Yes);

    dialogQty = dialog.addField(enumstr(NoYes));
    dialogQty.label("@SYS26378");

    if (bomCalcTable.Qty != salesLine.QtyOrdered)
    {
        dialogQty.value(NoYes::Yes);
    }
    else
    {
        dialogQty.value(NoYes::No);
        dialogQty.enabled(false);
    }

    if (bomVersionsEnabled)
    {
        dialogBOMIdMove = dialog.addField(enumstr(NoYes));
        if (pmfFormCtrl_BOMCalcTable.isFormulaEnabled())
        {
            dialogBOMIdMove.label("@PRO2117");
        }
        else
        {
            dialogBOMIdMove.label("@SYS28520");
        }
        dialogBOMIdMove.value(NoYes::Yes);
    }

    if (routeEnabled)
    {
        dialogRouteIdMove = dialog.addField(enumstr(NoYes));
        dialogRouteIdMove.label("@SYS28521");
        dialogRouteIdMove.value(NoYes::Yes);
    }
    if (routeEnabled || bomVersionsEnabled)
    {
        dialog.addGroup("@SYS2186");
    }

    if (bomVersionsEnabled)
    {
        dialogBOMId = dialog.addField(extendedtypestr(ItemBOMId));
        dialogBOMId.value(bomCalcTable.bomId);
        dialogBOMId.enabled(false);
    }
    if (routeEnabled)
    {
        dialogRouteId = dialog.addField(extendedtypestr(ItemRouteId));
        dialogRouteId.value(bomCalcTable.RouteId);
        dialogRouteId.enabled(false);
    }
    dialog.addGroup("@SYS14614");

    dialogQtySales = dialog.addField(extendedtypestr(UnitOfMeasureSymbol));
    dialogQtySales.label("@SYS11959");
    dialogQtySales.value(salesLine.SalesUnit);
    dialogQtySales.enabled(false);

    dialogQtySales = dialog.addField(extendedtypestr(InventQty));
    dialogQtySales.label("@SYS14578");
    dialogQtySales.value(qtySales);
    dialogQtySales.enabled(false);

    dialogPriceSales = dialog.addField(extendedtypestr(SalesPrice));
    dialogPriceSales.label("@SYS7222");
    dialogPriceSales.value(priceSales);
    dialogPriceSales.enabled(false);

    dialog.addGroup("@SYS16617");

    dialogQtySales = dialog.addField(extendedtypestr(UnitOfMeasureSymbol));
    dialogQtySales.label("@SYS11959");
    dialogQtySales.value(bomCalcTable.UnitId);
    dialogQtySales.enabled(false);

    dialogQtyInvent = dialog.addField(extendedtypestr(InventQty));
    dialogQtyInvent.label("@SYS14578");
    dialogQtyInvent.value(qtyInvent);
    dialogQtyInvent.enabled(false);

    dialogPriceInvent = dialog.addField(extendedtypestr(SalesPrice));
    dialogPriceInvent.label("@SYS7222");
    dialogPriceInvent.value(priceInvent);
    dialogPriceInvent.enabled(false);

    if (dialog.run())
    {
        if (salesLine.salesTable().checkUpdate())
        {
            ttsbegin;

            if (dialogSalesPrice.value() == NoYes::Yes)
            {
                this.initializeSalesLinePrices(priceSales);
            }
            if (bomVersionsEnabled && dialogBOMIdMove.value() == NoYes::Yes)
            {
                salesLine.ItemBOMId = bomCalcTable.bomId;
            }

            if (routeEnabled && dialogRouteIdMove.value() == NoYes::Yes)
            {
                salesLine.ItemRouteId = bomCalcTable.RouteId;
            }

            if (dialogQty.value() == NoYes::Yes)
            {
                salesLine.SalesQty   = qtySales;
            }

            if (dialogQty.value() == NoYes::Yes || dialogSalesPrice.value() == NoYes::Yes)
            {
                salesLine.salesQtyModified();
                salesLine.write();
                if (formSalesTable && FormDataUtil::getFormDataSource(salesLine))
                {
                    FormDataUtil::getFormDataSource(salesLine).reread();
                    FormDataUtil::getFormDataSource(salesLine).refresh();
                }
            }

            ttscommit;
        }
        element.close();
    }
}

/// <summary>
/// Initializes the sales line prices.
/// </summary>
/// <param name = "_priceSales">The accounting currency amount used to calculate the transaction currency amount.</param>
protected void initializeSalesLinePrices(Amount _priceSales)
{
    salesLine.SalesPrice = ExchangeRateHelper::curPrice(_priceSales, salesLine.CurrencyCode);
    salesLine.CostPrice  = bomCalcTable.costPricePcs();
}

