/// <summary>
/// Initialize sales and cost price for quotation line.
/// </summary>
/// <param name = "_priceSales">The accounting currency amount used to calculate the transaction currency amount.</param>
protected void initalizeSalesAndCostPriceForQuotationLine(Amount _priceSales)
{
    salesQuotationLine.SalesPrice = ExchangeRateHelper::curPrice(_priceSales, salesQuotationLine.CurrencyCode);
    salesQuotationLine.CostPrice  = bomCalcTable.costPricePcs();
}

void transferToServiceOrderLine()
{
    Dialog              dialog = new Dialog("@SYS96666"); // Transfer to service order
    DialogField         dialogSalesPrice;
    DialogField         dialogQty;

    DialogField         dialogPriceInvent;
    DialogField         dialogPriceService;
    DialogField         dialogQtyInvent;
    DialogField         dialogQtyService;

    InventTableModule   inventTableModule;

    Qty                 qtyInvent;
    Qty                 qtyService;
    Amount              priceInvent;
    Amount              priceService;

    boolean             updateLine = false;

    inventTableModule   = InventTableModule::find(bomCalcTable.ItemId,ModuleInventPurchSales::Sales);
    qtyInvent           = bomCalcTable.Qty;
    qtyService          = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(bomCalcTable.ItemId,
                                                                                                bomCalcTable.InventDimId,
                                                                                                qtyInvent,
                                                                                                bomCalcTable.UnitId,
                                                                                                serviceOrderLine.Unit,
                                                                                                NoYes::Yes);

    priceInvent         = CurrencyExchangeHelper::price(bomCalcTable.salesPricePcs());
    priceService        = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(bomCalcTable.ItemId,
                                                                                                bomCalcTable.InventDimId,
                                                                                                bomCalcTable.salesPricePcs(),
                                                                                                serviceOrderLine.Unit,
                                                                                                bomCalcTable.UnitId,
                                                                                                NoYes::Yes,
                                                                                                NoYes::No);

    dialogSalesPrice = dialog.addField(enumStr(NoYes));
    dialogSalesPrice.label("@SYS26377");  // Transfer price
    dialogSalesPrice.value(NoYes::Yes);

    dialogQty = dialog.addField(enumStr(NoYes));
    dialogQty.label("@SYS26378");  //Transfer quantity

    if (bomCalcTable.Qty != serviceOrderLine.Qty)
    {
        dialogQty.value(NoYes::Yes);
    }
    else
    {
        dialogQty.value(NoYes::No);
        dialogQty.enabled(false);
    }

    dialog.addGroup("@SYS14614");   // Sales unit

    dialogQtyService = dialog.addField(extendedTypeStr(UnitOfMeasureSymbol));
    dialogQtyService.label("@SYS11959");  // Unit
    dialogQtyService.value(serviceOrderLine.Unit);
    dialogQtyService.enabled(false);

    dialogQtyService = dialog.addField(extendedTypeStr(InventQty));
    dialogQtyService.label("@SYS14578");  // Quantity
    dialogQtyService.value(qtyService);
    dialogQtyService.enabled(false);

    dialogPriceService = dialog.addField(extendedTypeStr(SalesPrice));
    dialogPriceService.label("@SYS7222"); // Sales price per unit.
    dialogPriceService.value(priceService);
    dialogPriceService.enabled(false);

    dialog.addGroup("@SYS16617");   // Inventory unit

    dialogQtyService = dialog.addField(extendedTypeStr(UnitOfMeasureSymbol));
    dialogQtyService.label("@SYS11959");
    dialogQtyService.value(bomCalcTable.UnitId);
    dialogQtyService.enabled(false);

    dialogQtyInvent = dialog.addField(extendedTypeStr(InventQty));
    dialogQtyInvent.label("@SYS14578");
    dialogQtyInvent.value(qtyInvent);
    dialogQtyInvent.enabled(false);

    dialogPriceInvent = dialog.addField(extendedTypeStr(SalesPrice));
    dialogPriceInvent.label("@SYS7222");
    dialogPriceInvent.value(priceInvent);
    dialogPriceInvent.enabled(false);

    if (dialog.run())
    {
        // if modifications to service order line are allowed
        if (serviceOrderLine.checkStatusAllowModify()
            && SMAServiceOrderTable::find(serviceOrderLine.ServiceOrderId).checkStageAllowModify())
        {
            if (dialogSalesPrice.value() == NoYes::Yes)
            {
                serviceOrderLine.ProjSalesPrice = priceService;
                serviceOrderLine.ProjCostPrice  = bomCalcTable.costPricePcs();
                updateLine = true;
            }

            if (dialogQty.value() == NoYes::Yes)
            {
                serviceOrderLine.Qty   = qtyService;
                updateLine = true;
            }

            if (updateLine)
            {
                // save changes to Serviceorder line
                ttsbegin;
                serviceOrderLine.write();
                ttscommit;
                // refresh form view
                if (formServiceOrderTable && FormDataUtil::getFormDataSource(serviceOrderLine))
                {
                    FormDataUtil::getFormDataSource(serviceOrderLine).reread();
                    FormDataUtil::getFormDataSource(serviceOrderLine).refresh();
                }
            }
        }
        element.close();
    }
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }
            inventDimParmShow.initProductDimensionsAllGroups();
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmShow);
            // No break
        case InventDimFormDesignUpdate::Active:
            inventDimParmEnabled = EcoResProductDimGroupSetup::newItemId(bomCalcTable.ItemId).inventDimParmActiveDimensions();
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

void linkActive()
{
    InventDim       parentInventDim;

    if (element.args())
    {
        if (element.args().dataset() == tablenum(SalesLine))
        {
            salesLine           = element.args().record();
            salesLine           = element.args().record();
            parentItemId        = salesLine.ItemId;
            parentInventDimId   = salesLine.InventDimId;
            parentInventDim     = salesLine.inventDim();
            parentBOMId         = salesLine.ItemBOMId
                ? salesLine.ItemBOMId
                : salesLine.inventTable().bomId(salesLine.ConfirmedDlv ? salesLine.ConfirmedDlv :
                    InventOrderEntryDeadlineTable::effectiveOrderDate(parentInventDim.InventSiteId,CustTable::find(salesLine.CustAccount).OrderEntryDeadlineGroupId), salesLine.QtyOrdered, parentInventDim);
            parentRouteId       = salesLine.ItemRouteId
                ? salesLine.ItemRouteId
                : salesLine.inventTable().routeId(salesLine.ConfirmedDlv ? salesLine.ConfirmedDlv :
                    InventOrderEntryDeadlineTable::effectiveOrderDate(parentInventDim.InventSiteId,CustTable::find(salesLine.CustAccount).OrderEntryDeadlineGroupId), salesLine.QtyOrdered, parentInventDim);
        }
        else if (element.args().dataset() == tablenum(SalesQuotationLine))
        {
            salesQuotationLine  = element.args().record();
            parentItemId        = salesQuotationLine.ItemId;
            parentInventDimId   = salesQuotationLine.InventDimId;
            parentInventDim     = salesQuotationLine.inventDim();
            parentBOMId         = salesQuotationLine.ItemBOMId
                ? salesQuotationLine.ItemBOMId
                : salesQuotationLine.inventTable().bomId(salesQuotationLine.ConfirmedDlv ? salesQuotationLine.ConfirmedDlv :
                    InventOrderEntryDeadlineTable::effectiveOrderDate(parentInventDim.InventSiteId,CustTable::find(salesQuotationLine.CustAccount).OrderEntryDeadlineGroupId), salesQuotationLine.QtyOrdered, parentInventDim);
            parentRouteId       = salesQuotationLine.ItemRouteId
                ? salesQuotationLine.ItemRouteId
                : salesQuotationLine.inventTable().routeId(salesQuotationLine.ConfirmedDlv ? salesQuotationLine.ConfirmedDlv :
                    InventOrderEntryDeadlineTable::effectiveOrderDate(parentInventDim.InventSiteId,CustTable::find(salesQuotationLine.CustAccount).OrderEntryDeadlineGroupId), salesQuotationLine.QtyOrdered, parentInventDim);
        }

        else if (bomDesignerBOMCalc)
        {
            parentItemId        = bomDesignerBOMCalc.parmItemId();
            parentInventDimId   = bomDesignerBOMCalc.parmInventDimId();
            parentInventDim     = InventDim::find(parentInventDimId);
            parentBOMId         = bomDesignerBOMCalc.parmBOMId();
            parentRouteId       = bomDesignerBOMCalc.parmRouteId();
        }
        else if (element.args().dataset() == tablenum(SMAServiceOrderLine))
        {
            serviceOrderLine    = element.args().record();
            parentItemId        = serviceOrderLine.ItemId;
            parentInventDimId   = serviceOrderLine.InventDimId;
            parentInventDim     = serviceOrderLine.inventDim();
            parentBOMId         = InventTable::find(serviceOrderLine.ItemId).bomId(DateTimeUtil::date(SMAServiceOrderTable::find(serviceOrderLine.ServiceOrderId).ServiceDateTime), serviceOrderLine.Qty, parentInventDim);
            parentRouteId       = InventTable::find(serviceOrderLine.ItemId).routeId(DateTimeUtil::date(SMAServiceOrderTable::find(serviceOrderLine.ServiceOrderId).ServiceDateTime), serviceOrderLine.Qty, parentInventDim);
        }
        else if (element.args().dataset() == tableNum(PdsComdPricingMarginLine))
        {
            comdPricingMarginLine   = element.args().record();
            parentItemId            = comdPricingMarginLine.ItemId;
            parentInventDimId       = comdPricingMarginLine.InventDimId;
            parentBOMId             = comdPricingMarginLine.BOMId;
            parentRouteId           = comdPricingMarginLine.RouteId;
            ctrlCalculate.enabled(false);
        }
    }
    else
    {
        parentItemId        = '';
        parentInventDimId   = '';
        parentInventDim.clear();
        parentBOMId         = '';
        parentRouteId       = '';
    }

    super();
}

void init()
{
    Query                   q = new Query();
    QueryBuildDataSource    qB;
    BOMCalcType             bomCalcType;

    super();
    if (bomDesignerBOMCalc)
    {
        bomCalcType = BOMCalcType::Version;
        qB = q.addDataSource(tableNum(BOMCalcTable));
        qB.addRange(fieldNum(BOMCalcTable,ItemId)).value(queryValue(bomDesignerBOMCalc.parmItemId()));
        this.query(q);
    }
    else
    {
        bomCalcType = BOMCalcType::Order;
    }

    if (bomCalcTableFromLookup)
    {
        this.query().dataSourceTable(tableNum(BOMCalcTable)).addRange(fieldNum(BOMCalcTable, PriceCalcId)).value(bomCalcTableFromLookup.PriceCalcId);
    }
    else
    {
        this.query().dataSourceTable(tableNum(BOMCalcTable)).addRange(fieldNum(BOMCalcTable, bomCalcType)).value(queryValue(bomCalcType));
    }

    this.query().dataSourceNo(1).addSortField(fieldNum(BOMCalcTable,PriceCalcId),SortOrder::Ascending);
    this.query().dataSourceNo(1).addSortField(fieldNum(BOMCalcTable,ItemId),SortOrder::Ascending);

    bomCalcTable_ds.cacheAddMethod(tableMethodStr(BOMCalcTable, infologImage));
}

display UnitOfMeasureSymbol salesUnit(BOMCalcTable _bomCalcTable)
{
    return salesUnit;
}

int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    btnWarningsLog.enabled(bomCalcTable.isInfolog());

    return ret;
}

display SalesPrice salesPrice(BOMCalcTable _bomCalcTable)
{
    return EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(_bomCalcTable.ItemId,
                                                                                 _bomCalcTable.InventDimId,
                                                                                 bomCalcTable.salesPricePcs(),
                                                                                 salesUnit,
                                                                                 (_bomCalcTable.UnitId),
                                                                                 NoYes::Yes,
                                                                                 NoYes::No);
}

display InventQty salesQty(BOMCalcTable _bomCalcTable)
{
    return EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(_bomCalcTable.ItemId,
                                                                                 _bomCalcTable.InventDimId,
                                                                                 bomCalcTable.Qty,
                                                                                 _bomCalcTable.UnitId,
                                                                                 salesUnit,
                                                                                 NoYes::Yes);
}

void clicked()
{
    super();

    bomCalcTable_ds.executeQuery();
}

void clicked()
{
    // Enable/disable Transfer to Order
    if (salesLine && bomCalcTable.RecId && bomCalcTable.InventDimId == salesLine.InventDimId)
    {
        transferToSales.enabled(true);
    }
    else
    {
        transferToSales.enabled(false);
    }

    // Enable/disable Transfer to Quotation
    if (salesQuotationLine && bomCalcTable.RecId && bomCalcTable.InventDimId == salesQuotationLine.InventDimId)
    {
        transferToSalesQuotation.enabled(true);
    }
    else
    {
        transferToSalesQuotation.enabled(false);
    }

    // Enable/disable Transfer to Service Order
    if (serviceOrderLine && bomCalcTable.RecId && bomCalcTable.InventDimId == serviceOrderLine.InventDimId)
    {
        transferToServiceOrder.enabled(true);
    }
    else
    {
        transferToServiceOrder.enabled(false);
    }

    super();
}

void clicked()
{
    super();

    element.transferToSalesLine();
}

void clicked()
{
    super();

    element.transferToSalesQuotationLine();
}

void clicked()
{
    super();

    element.transferToServiceOrderLine();
}

void clicked()
{
    super();

    bomCalcTable.showInfolog();
}

/// <summary>
/// Show the warning/error messages when clicked on the image.
/// </summary>
public void clicked()
{
    super();

    bomCalcTable.showInfolog();
}
