public PriceCalcId actualPriceCalcId()
{
    PriceCalcId priceCalcId = element.priceCalcId();

    if (#PmfEnabled && pmfFormulaCoBy.ItemId)
    {
        priceCalcId = BOMCalcTable::actualPriceCalcId(priceCalcId, pmfFormulaCoBy.ItemId, pmfFormulaCoBy.InventDimId);
    }

    return priceCalcId;
}

void addSorting(Query query)
{
    query.dataSourceTable(tableNum(BOMCalcTransDetailUnionAll)).addSortField(fieldNum(BOMCalcTransDetailUnionAll,LineNum), SortOrder::Ascending);
}

/// <summary>
/// Clears the details.
/// </summary>
public void clearDetails()
{
    includeDetails.value(false);

    element.setFormControlVisibility();

    viewDetail = includeDetails.checked();

    if (bomCalcProviderId)
    {
        BOMCalcProvider::cleanUpByTransactionId(bomCalcProviderId);

        bomCalcProviderId = 0;

        transactionIdDetailRange.value(queryValue(bomCalcProviderId));
    }
}

/// <summary>
/// Closes the form and removes details if exist.
/// </summary>
public void close()
{
    if (bomCalcProviderId)
    {
        BOMCalcProvider::cleanUpByTransactionId(bomCalcProviderId);
    }

    super();
}

void init()
{
    if (!element.args() ||
        (element.args().dataset() != tableNum(BOMCalcTable) &&
         element.args().dataset() != tableNum(InventItemPrice) &&
         element.args().dataset() != tableNum(InventItemPriceSim)))
    {
        throw error(Error::missingFormActiveBuffer(element.name()));
    }

    pmfFormCtrl_BOMCalcTrans = PmfFormCtrl_BOMCalcTrans::newFromForm(element);
    costSheetPanel = CostSheetPanel_BOMCalcTrans::newForm(element,
        element.controlId(formControlStr(BOMCalcTrans, CostSheetPanelTabPage)));

    super();

    costSheetPanel.init();         

    pmfFormCtrl_BOMCalcTrans.setDatasources(bomCalcTrans);
    pmfFormCtrl_BOMCalcTrans.initPost();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

str nodeTxt(BOMCalcTransDetailUnionAll _bomCalcTrans)
{
    DictEnum    dictEnum = new DictEnum(enumNum(ItemCalcType));
    str         s;

    s = dictEnum.value2Label(enum2int(_bomCalcTrans.CalcType)) +
        '/' +
        (_bomCalcTrans.CalcType == ItemCalcType::CostGroup ? _bomCalcTrans.CostGroupId : _bomCalcTrans.Resource);

    return s;
}

PriceCalcId priceCalcId(boolean refresh = false)
{
    PriceCalcId         priceCalcId;
    BOMCalcTable        bomCalcTable;
    InventItemPriceMap  inventItemPriceMap;

    if (element.args() == null || element.args().record() == null)
    {
        return '';
    }

    if (priceCalcIdArgs
        && !refresh)
    {
        return priceCalcIdArgs;
    }

    switch (element.args().dataset())
    {
        case tableNum(BOMCalcTable):
            bomCalcTable = element.args().record();
            priceCalcId = bomCalcTable.PriceCalcId;
            break;

        case tableNum(InventItemPriceSim),
             tableNum(InventItemPrice):
            inventItemPriceMap = element.args().record();
            priceCalcId = inventItemPriceMap.PriceCalcId;
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    if (#PmfEnabled)
    {
        if (!bomCalcTable)
        {
            bomCalcTable = BOMCalcTable::find(priceCalcId);
        }
        if (bomCalcTable.PmfParentCalcId && BOMCalcTable::exist(bomCalcTable.PmfParentCalcId))
        {
            // Use the main item calculation
            priceCalcId = bomCalcTable.PmfParentCalcId;
        }
    }

    priceCalcIdArgs = priceCalcId;

    return priceCalcId;
}

public Common record()
{
    Args    args    = element.args();
    Common  record  = !args ? null : args.record();

    return record;
}

/// <summary>
/// Refreshes the BOMCalcTransDetail data source.
/// </summary>
public void refreshDetails()
{
    bomCalcTrans_ds.executeQuery();
}

public void run()
{
    element.setPriceCalcIdRangeValue();

    super();
}

/// <summary>
/// Sets the range value for calculation transactions.
/// </summary>
/// <remarks>
/// The calculation details will be created the first time it will be shown.
/// </remarks>
public void setDetailRangeValue()
{
    viewDetail = includeDetails.checked();

    element.setFormControlVisibility();

    if (viewDetail
        && !pmfFormulaCoBy.isByOrCoProduct())
    {
        if (!bomCalcProviderId)
        {
            bomCalcProviderId = BOMCalcProvider::createDetails(priceCalcIdRange.value(), NoYes::Yes);
        }

        transactionIdDetailRange.value(queryValue(bomCalcProviderId));
    }
    else
    {
        transactionIdDetailRange.value(queryValue(0));
    }
}

/// <summary>
/// Sets form control visibility.
/// </summary>
public void setFormControlVisibility()
{
    if (priceCalcIdDetails != priceCalcIdRange.value())
    {
        canShowDetails = BOMCalcProvider::canShowDetails(priceCalcIdRange.value());
        priceCalcIdDetails = priceCalcIdRange.value();
    }

    if (pmfFormulaCoBy.isByOrCoProduct())
    {
        includeDetails.value(false);
        viewDetail = includeDetails.checked();
    }

    includeDetails.enabled(!pmfFormulaCoBy.isByOrCoProduct());
    includeDetails.visible(canShowDetails);
    costSheetPanelTabPage.visible(!viewDetail);
    bomCalcTrans_CostCalculationMethod.visible(viewDetail);

    if (element.args().dataset() == tableNum(InventItemPriceSim)
        || element.args().dataset() == tableNum(BOMCalcTable))
    {
        CostPriceCompare.visible(true);
    }
}

public void setPriceCalcIdRangeValue()
{
    InventItemPriceMap  inventItemPriceMap;
    BOMCalcTable        bomCalcTable;
    PriceCalcId priceCalcId;

    if (#PmfEnabled)
    {
        priceCalcId = element.actualPriceCalcId();
        priceCalcIdRange.value(priceCalcId ? priceCalcId : SysQuery::valueEmptyString());
        return;
    }

    if (!element.args().dataset())
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    switch (element.args().dataset())
    {
        case tableNum(BOMCalcTable):
            bomCalcTable = element.args().record();
            priceCalcIdRange.value(bomCalcTable.PriceCalcId ? bomCalcTable.PriceCalcId : SysQuery::valueEmptyString());
            break;

        case tableNum(InventItemPriceSim),
             tableNum(InventItemPrice):
            inventItemPriceMap = element.args().record();
            priceCalcIdRange.value(inventItemPriceMap.PriceCalcId ? inventItemPriceMap.PriceCalcId : SysQuery::valueEmptyString());
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }
}

private void updateCalcType(ItemCalcType _calcType)
{
    switch (_calcType)
    {
        case ItemCalcType::Purchase:
            bomCalcTrans_ds.object(fieldNum(BOMCalcTransDetailUnionAll, OprId)).visible(false);
            bomCalcTrans_ds.object(fieldNum(BOMCalcTransDetailUnionAll, OprNum)).visible(false);
            break;
        default:
            bomCalcTrans_ds.object(fieldNum(BOMCalcTransDetailUnionAll, OprId)).visible(true);
            bomCalcTrans_ds.object(fieldNum(BOMCalcTransDetailUnionAll, OprNum)).visible(true);
            break;
    }
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl::newFromForm(element);
            }

            inventDimFormSetup.parmSkipOnHandLookUp(true);
            inventDimParmShow.initDimBOMAll();
            inventDimParmShow                       = inventDimFormSetup.parmDimParmVisibleGrid();
            inventDimParmShow.ConfigIdFlag          = true;
            inventDimParmShow.InventLocationIdFlag  = true;
            //fallthrough
        case InventDimFormDesignUpdate::Active,
            InventDimFormDesignUpdate::FieldChange :
            InventDimGroupSetup::newInventTable(pmfFormulaCoBy.inventTable()).inventDimParmActive(inventDimParmEnabled);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Adds the View Details form option to the unit of measure field.
/// </summary>
/// <param name="_value">
/// The value to use for the lookup when the View Details form opens.
/// </param>
public void viewDetailOperationId(UnitOfMeasureSymbol _value)
{
    Args            args = new Args();
    FormRun         formRun;
    MenuFunction    menuFunction = new MenuFunction(menuitemDisplayStr(RouteOprTable), MenuItemType::Display);

    args.lookupField(fieldNum(RouteOprTable, OprId));
    args.lookupValue(_value);

    formRun = menuFunction.create(args);
    formRun.run();
}

/// <summary>
/// Adds the View Details form option to the resource field.
/// </summary>
/// <param name="_value">
/// The value to use for the lookup when the View Details form opens.
/// </param>
public void viewDetailResource(BOMCalcResource _value)
{
    switch (bomCalcTrans.CalcType)
    {
        case ItemCalcType::Production:
        case ItemCalcType::Item:
        case ItemCalcType::BOM:
        case ItemCalcType::Service:
        case ItemCalcType::Burden:
            InventTable::jumpRefItemId(_value);
            break;

        case ItemCalcType::Setup:
        case ItemCalcType::Process:
        case ItemCalcType::Qty:
            WrkCtrTable::jumpRefWrkCtrId(_value);
            break;
    }
}

/// <summary>
/// Adds the View Details form option to the unit of measure field.
/// </summary>
/// <param name="_value">
/// The value to use for the lookup when the View Details form opens.
/// </param>
public void viewDetailUnitId(UnitOfMeasureSymbol _value)
{
    Args            args = new Args();
    FormRun         formRun;
    MenuFunction    menuFunction = new MenuFunction(menuitemDisplayStr(UnitOfMeasure), MenuItemType::Display);

    args.lookupField(fieldNum(UnitOfMeasure, Symbol));
    args.lookupValue(_value);

    formRun = menuFunction.create(args);
    formRun.run();
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        pmfCoProductItemPrice.enabled(pmfFormulaCoBy.ProductType == PmfProductType::Co_Product);

        element.updateDesign(InventDimFormDesignUpdate::Active);

        if (!linkActivePmfFormulaCoBy)
        {
            element.setPriceCalcIdRangeValue();
            element.refreshDetails();
        }

        linkActivePmfFormulaCoBy = false;
    }

    return ret;
}

public display UnitOfMeasureSymbol unit(PmfFormulaCoBy _pmfFormulaCoBy)
{
    return InventTable::find(_pmfFormulaCoBy.ItemId).inventUnitId();
}

public void linkActive()
{
    PmfFormulaCoByCalc  formulaCoByCalc;

    if (#PMFEnabled)
    {
        formulaCoByCalc = PmfFormulaCoByCalc::construct(element.record());
        if (formulaCoByCalc)
        {
            pmfFormulaCoBy.setTmpData(formulaCoByCalc.formulaCoByDataSet(element.priceCalcId(true)));
        }
    }
    else
    {
        element.priceCalcId(true);
    }

    super();

    linkActivePmfFormulaCoBy = true;

    element.clearDetails();
    element.setPriceCalcIdRangeValue();
    element.refreshDetails();
}

public void init()
{
    super();

    if (#PMFEnabled)
    {
        pmfFormulaCoBy.setTmp();

        this.query().dataSourceTable(this.table()).clearDynalinks();
    }
}

int active()
{
    int ret;

    ret = super();

    btnTradeArgeements.enabled(bomCalcTrans.CalcType == ItemCalcType::Production
                                || bomCalcTrans.CalcType == ItemCalcType::Item
                                || bomCalcTrans.CalcType == ItemCalcType::BOM);

    btnWarningsLog.enabled(bomCalcTrans.Infolog ? true : false);

    return ret;
}

public void executeQuery()
{
    element.setDetailRangeValue();

    super();

    if (!viewDetail)
    {
        costSheetPanel.parmPriceCalcId(element.actualPriceCalcId());

        costSheetPanel.fill();
    }
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(BOMCalcTransDetailUnionAll)).clearDynalinks();

    priceCalcIdRange = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(BOMCalcTransDetailUnionAll)), fieldNum(BOMCalcTransDetailUnionAll, PriceCalcId));
    transactionIdDetailRange = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(BOMCalcTransDetailUnionAll)), fieldNum(BOMCalcTransDetailUnionAll, CreatedTransactionIdDetail));
    transactionIdDetailRange.status(RangeStatus::Hidden);

    element.setPriceCalcIdRangeValue();
    element.addSorting(this.query());
}

