Method.Source:
[Form]
public class BOMChangeLine extends FormRun
{
    #resAppl
    BOMChangeItem               runBase;
    InventDimCtrl_Frm           inventDimFormSetup;
    PmfFormCtrl_BOMChangeLine   pmfFormCtrl_BOMChangeLine;

}

public void init()
{
    QueryBuildRange         queryBuildRangeItemId;
    ItemId                  fromItemId;

    pmfFormCtrl_BOMChangeLine = PmfFormCtrl_BOMChangeLine::newFromForm(element);

    if (!element.args() || !element.args().caller())
    {
        throw error(strfmt("@SYS22862",element.name()));
    }

    if (!(element.args().caller().runbase() is BOMChangeItem))
    {
        throw error(strfmt("@SYS22456", element.name(), classstr(BOMChangeItem)));
    }
    runBase = element.args().caller().runbase();

    runBase.getFromDialog();

    if (!runBase.validate())
        throw warning("@SYS18447");

    runBase.createTmpBOM();

    super();

    queryBuildRangeItemId = runBase.queryRun().query().dataSourceTable(tablenum(BOM)).findRange(fieldnum(BOM,ItemId));

    if (queryBuildRangeItemId) // user might remove ItemId range from the query
    {
        fromItemId  = queryBuildRangeItemId.value();
    }
    pmfFormCtrl_BOMChangeLine.setDatasources(bom);

    if (pmfFormCtrl_BOMChangeLine.isFormulaEnabled())
    {
        element.design().caption("@PRO1014");
    }

    if (runBase.parmUpdateMethod() == OverwriteAppend::Overwrite)
    {
        bom_FromDate.visible(false);
        bom_ToDate.visible(false);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
    pmfFormCtrl_BOMChangeLine.initPost();
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }
            inventDimFormSetup.parmSkipOnHandLookUp(true);
            inventDimParmShow.initDimBOMAll();
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmShow);

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange :
            inventDimParmEnabled.initDimBOMEnable(InventDimGroupSetup::newItemId(bom.ItemId), bom);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

public void init()
{
    super();

    if (runBase)
    {
        bom.setTmp();
        bom.checkRecord(false);
        bom.setTmpData(runBase.parmTmpBom());
    }
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    bom.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    return ret;
}

public void init()
{
    super();

    if (runBase)
    {
        inventDim.setTmp();
        inventDim.checkRecord(false);
        inventDim.setTmpData(runBase.parmTmpInventDim());
    }
}

public boolean validateWrite()
{
    boolean ret;

    if (bom.orig().InventDimId != bom.InventDimId)
    {
        if (!runBase.existInventDimId(bom.InventDimId))
        {
            inventdim.InventDimId = bom.InventDimId;
            inventDim.insert(true);
        }
    }

    ret = super();

    return ret;
}
