BOMId bomId()
{
    Object caller;

    if (bomVersion.bomId)
    {
        return bomVersion.bomId;
    }
    else
    {
        caller = element.args().caller();
        if (caller)
        {
            if (SysDictClass::isEqualOrSuperclass(classIdGet(caller),classNum(FormRun)))
            {
                if (formHasMethod(caller,identifierStr(bomId)))
                {
                    return caller.bomId();
                }
            }
            else
            {
                return caller.bomId();
            }
        }
    }

    return '';
}

/// <summary>
/// Sets the filter on the BOMVersion InventDim, to only include records applicable to the specified product dimension values.
/// </summary>
/// <param name = "_inventDimProduct">The <c>InventDim</c> record containing product dimension values to filter on.</param>
protected void setBOMVersionProductDimFilter(InventDim _inventDimProduct)
{
    QueryBuildDataSource qbdsInventDimBOMVersion = InventDimBOMVersion_ds.queryBuildDataSource();
    qbdsInventDimBOMVersion.clearRanges();

    if (_inventDimProduct.inventDimId)
    {
        InventDimensionEnumerator enum = InventProductDimension::getEnumeratorForEnabledDimensions();
        while (enum.moveNext())
        {
            FieldId fieldId = enum.currentInventDimFieldId();

            // Either it has to be the specific value or blank
            qbdsInventDimBOMVersion.addRange(fieldId).value(queryValue(_inventDimProduct.(fieldId)));
            qbdsInventDimBOMVersion.addRange(fieldId).value(SysQuery::valueEmptyString());
        }
    }   
}

