/// <summary>
/// Create a new BOM version directly through the form data source.
/// </summary>
/// <param name = "_newBOMTable">The BOM to initialize the new version from.</param>
/// <param name = "_dialogCopy">Copy details from another BOM?</param>
/// <param name = "_copyTempVersion"><c>BOMVersion</c> to copy item details from.</param>
public void createNewBomVersion(BOMTable _newBOMTable, NoYes _dialogCopy, BOMVersion _copyTempVersion)
{
    if (RouteBOMInventProdRemoveFiltersFlight::instance().isEnabled())
    {
        bomVersion_ds.removeFilter();
    }

    bomVersion_ds.create();

    if (_dialogCopy)
    {
        bomVersion.initBomVersionCopy(_copyTempVersion);

        InventDim inventDim;
        inventDim = InventDim::find(_copyTempVersion.InventDimId);
        inventDim.InventSiteId  = _newBOMTable.SiteId;
        inventDim = InventDim::findOrCreate(inventDim);
        bomVersion.InventDimId = inventDim.InventDimId;
    }
    else
    {
        bomVersion.Active = NoYes::No;
        if (!bomVersion.ItemId && _copyTempVersion.ItemId)
        {
            bomVersion.ItemId = _copyTempVersion.ItemId;
        }
    }

    bomVersion.initFromBOMTable(_newBOMTable);
    bomVersion_ds.write();

    if (_dialogCopy && callerInventTable.isFormulaAllowed())
    {
        PmfFormulaCoBy::postUpdateBOMVersion(bomVersion);
    }

    bomVersion_ds.refresh();
}

