public boolean canClose()
{
    SysOperationProgress waitDialog;
    boolean ret = super();
    boolean bomCheckedOk = true;

    if (needCheckBOM && bom.bomId && BOMParameters::find().mustCheckBOMCircularityBOM())
    {
        waitDialog = BOMHierarchyCheck::showWaitDialog();
        bomCheckedOk = BOMHierarchyCheck::checkBOMCircularity(bom.bomId);
        BOMTable::setCheckBOM(bom.bomId, bomCheckedOk);
        waitDialog.hide();
    }

    // a box needs to be opened in order to prevent the form from closing without informing the user of the BOM circulation
    if (!bomCheckedOk && Box::yesNo("@SYS122835", DialogButton::No) == DialogButton::Yes)
    {
        bomCheckedOk = true;
    }

    needCheckBOM = false;

    return ret && bomCheckedOk;
}

void checkBOM(BOMId bomId)
{
    SysOperationProgress waitDialog;
    boolean              checkCircularity;

    if (!needCheckBOM || ! bomId)
    {
        return;
    }
    if (! BOMTable::find(bomId).CheckBOM)
    {
        return;
    }

    checkCircularity = !BOMParameters::find().mustCheckCircularityManual();

    if (checkCircularity)
    {
        waitDialog = BOMHierarchyCheck::showWaitDialog();
    }

    BOMHierarchyCheck::checkBOM(bomId, checkCircularity);

    if (checkCircularity)
    {
        waitDialog.hide();
    }

    needCheckBOM = false;
}

void close()
{
    if (bomVersion.bomId && bomVersion.bomId != naStr())
    {
        element.checkBOM(bomVersion.bomId);
    }

    super();
}

