/// <summary>
/// Get the list of <c>BOMTable</c> records from the selected <c>BOMVersion</c> records.
/// </summary>
/// <returns>
/// A list of <c>BOMTable</c> records
/// </returns>
List getBOMTableRecords()
{
    List            selectedRecords = new List(Types::Record);
    BOMVersion      bomVersionSelected;
    BOMTable        bomTable;

    // Get the BOMTable records for each selected BOMVersion
    bomVersionSelected = BOMVersion_ds.getFirst(true);
    if (!bomVersionSelected)
    {
        bomVersionSelected = BOMVersion_ds.cursor();
    }
    while (bomVersionSelected)
    {
        bomTable = bomVersionSelected.bomTable();
        selectedRecords.addEnd(bomTable);
        bomVersionSelected = BOMVersion_ds.getNext();
    }

    return selectedRecords;
}

void init()
{
    this.retrieveInventoryTableFromCaller();
    if (!callerInventTable)
    {
        throw error(Error::missingFormActiveBuffer(formstr(BOMConsistOf), tablestr(InventTable)));
    }

    pmfFormCtrl_BOMConsistOf_BOMVersion = PmfFormCtrl_BOMConsistOf_BOMVersion::newFromForm(element);
    pmfFormCtrl_BOMConsistOf_BOM = PmfFormCtrl_BOMConsistOf_BOM::newFromForm(element);

    super();

    NavigationList.visible(true);
    pmfFormCtrl_BOMConsistOf_BOMVersion.setDatasources(BOMVersion, InventDimBOMVersion);
    pmfFormCtrl_BOMConsistOf_BOM.setDatasources(bom, inventDimBOM);

    if (!callerInventTable.pdsCheckCWItemSetup())
    {
        throw Exception::Error;
    }
    if (!callerInventTable.isBOMAllowed() && !pmfFormCtrl_BOMConsistOf_BOMVersion.isFormulaEnabled())
    {
        throw error(strFmt("@SYS19326", callerInventTable.ItemId));
    }

    if (BOMParameters::find().MandatoryQtyAndDate)
    {
        bom_bomQuantity.mandatory(true);
        bomVersion_FromDate.mandatory(true);
        bomVersion_ToDate.mandatory(true);
    }

    element.updateDesignBOM(InventDimFormDesignUpdate::Init);
    element.updateDesignBOMVersion(InventDimFormDesignUpdate::Init, callerInventTable);

    pmfFormCtrl_BOMConsistOf_BOMVersion.initPost();
    pmfFormCtrl_BOMConsistOf_BOM.initPost();

    this.enableEngineeringChangeButtons();
}

/// <summary>
/// Retrieve inventory table from caller.
/// </summary>
protected void retrieveInventoryTableFromCaller()
{
    if (element.args().dataset() == tableNum(InventTable))
    {
        callerInventTable = element.args().record();
    }
    else if (element.args().dataset() == tableNum(InventDimCombination))
    {
        InventDimCombination inventDimCombination = element.args().record();
        callerInventTable = inventDimCombination.inventTable();
    }
    else if (element.args().dataset() == tableNum(BOM))
    {
        BOM billOfMaterials = element.args().record();
        callerInventTable = billOfMaterials.inventTable();
    }
}

