void linkActive()
{
    InventDim       inventDimPrimKey;

    if (element.args().dataset() == tableNum(InventDimCombination))
    {
        InventDimCombination inventDimCombination = element.args().record();
        inventDimPrimKey = inventDimCombination.inventDim();
    }

    element.setBOMVersionProductDimFilter(inventDimPrimKey);

    inventDimPrimKey.InventSiteId = callerInventTable.inventInventSiteId('', inventDimPrimKey.inventDimId);
    inventDimPrimKey = InventDim::findOrCreate(inventDimPrimKey);

    BOMVersion curActiveVersion = BOMVersion::findActive(callerInventTable.ItemId,
                                                      DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                                      callerInventTable.inventStandardQty(inventDimPrimKey.InventDimId),
                                                      inventDimPrimKey);

    if (FeatureStateProvider::isFeatureEnabled(DisableBomVersionButtonForInactiveBomItemsFlighting::instance())
        && !curActiveVersion)
    {
        inventDimPrimKey.clear();
        curActiveVersion = BOMVersion::findActive(callerInventTable.ItemId, dateNull(), maxUInt(), inventDimPrimKey);
        if (!curActiveVersion)
        {
            curActiveVersion = BOMVersion::selectBomVersion(callerInventTable.ItemId, dateNull(), maxUInt(), inventDimPrimKey, true, true, true, true, false);
        }
    }

    super();

    if (curActiveVersion)
    {
        bomVersion_ds.positionToRecord(curActiveVersion);
    }

    element.setBOMVersionApprove();
}

void init()
{
    super();

    if (element.args().parmEnumType() == enumnum(BOMVersionFilter)
       && element.args().parmEnum() == BOMVersionFilter::Active)
    {
        QueryBuildRange activeRange = this.queryBuildDataSource().addRange(fieldNum(BOMVersion, Active));
        activeRange.status(RangeStatus::Hidden);
        activeRange.value(queryValue(NoYes::Yes));
    }

    if (element.args().dataset() == tableNum(InventDimCombination))
    {
        this.queryBuildDataSource().addDynalink(fieldNum(BOMVersion, ItemId), element.args().record(), fieldNum(InventDimCombination, ItemId));
    }

    this.queryBuildDataSource().addSortIndex(indexNum(BOMVersion,ItemFromToIdx));
    pmfFormCtrl_BOMConsistOf_BOMVersion.dataSourceInitPost(bomVersion);
}

