int active()
{
    if (lastBOMId && lastBOMId != bomVersion.bomId)
    {
        element.checkBOM(lastBOMId);
    }

    int ret = super();
    pmfFormCtrl_BOMConsistOf_BOMVersion.parmBOM(bom);
    pmfFormCtrl_BOMConsistOf_BOMVersion.parmInventTable(bomVersion.inventTable());
    pmfFormCtrl_BOMConsistOf_BOMVersion.dataSourceActivePost();

    lastBOMId = bomVersion.bomId;

    element.setBOMEditButtons();
    element.setBOMVersionApprove();
    element.setBOMVersionActivate();
    element.setControlDecimals();
    element.enableBOMVersionFields();
    element.updateDesignBOMVersion(InventDimFormDesignUpdate::Active, bomVersion.inventTable());

    return ret;
}

