void init()
{
    super();

    var qb = this.queryBuildDataSource();
    qb.addSortIndex(indexNum(BOM,NumIdx));
    qb.addDynalink(fieldNum(BOM,bomId),bomVersion,fieldNum(BOMVersion,bomId));

    pmfFormCtrl_BOMConsistOf_BOM.dataSourceInitPost(bom);
    pmfFormCtrl_BOMConsistOf_BOM.parmBOMVersion(bomVersion);

    this.cacheAddMethod(tableMethodStr(BOM, pdsActiveQty));
    this.cacheAddMethod(tableMethodStr(BOM, ItemNameGrid), true);
}

