void clicked()
{
    super();

    if (!callerInventTable.RecId)
    {
        throw error(Error::missingFormActiveBuffer(element.name(), tableStr(InventTable)));
    }

    BOMId           fromBOMId;
    InventSiteId    fromSiteId;
    BOMVersion      copyTempVersion;
    if (bomVersion)
    {
        fromBOMId   = bomVersion.bomId;
        fromSiteId  = inventDimBOMVersion.InventSiteId;
        copyTempVersion.data(bomVersion);
    }

    BOMId       newBOMId;
    NoYes       dialogCopy;            
    InventDim   inventDim;
    BOMCreate bomCreate = element.constructBomCreateDialog();
    [newBOMId, inventDim.InventSiteId, dialogCopy] = bomCreate.promptCreateBOM(
        callerInventTable.ItemId,
        fromBOMId,
        fromSiteId,
        callerInventTable.isFormulaAllowed());

    if (dialogCopy && fromSiteId != inventDim.InventSiteId)
    {
        copyTempVersion.InventDimId = ''; // Inventory dimension will be initialized from the BOM
    }

    if (dialogCopy
        && callerInventTable.isFormulaAllowed()
        && bomCreate.parmBOMCopy()
        && bomCreate.parmBOMCopy().parmPmfFromVerRecId())
    {
        copyTempVersion.data(BOMVersion::findRecId(bomCreate.parmBOMCopy().parmPmfFromVerRecId()));

        if (BOMConsistOfCopyFormulaForCWItemFlightV2::instance().isEnabled() && (callerInventTable.ItemId != copyTempVersion.ItemId) && PdsGlobal::pdsIsCWItem(callerInventTable.ItemId))
        {
            copyTempVersion.initFromInventTable(callerInventTable);
        }
        else
        {
            copyTempVersion.setItemId(callerInventTable.ItemId);
        }
    }

    BOMTable newBOMTable;
    if (newBOMId)
    {
        newBOMTable = BOMTable::find(newBOMId);
        element.createNewBomVersion(newBOMTable, dialogCopy, copyTempVersion);
    }

    element.setBOMVersionApprove();

    if (newBOMTable)
    {
        element.navigateToBOMTable(newBOMTable, OpenMode::Edit);

        if (bomCreate.isSpecifiedBOMAlreadyExisting())
        {
            info(strFmt("@SCM:Message_BOMAlreadyExistNewBOMVersionCreated", newBOMTable.BOMId));
        }
    }
}

