void doLinkActive(Common _caller)
{
    SalesLine   salesLine;
    PurchLine   purchLine;
    InventDim   inventDim;
    BOMTable    bomTable;

    void setupFromInventDim()
    {
        inventDim               = InventDim::findOrCreate(inventDim);

        setup.bomRouteInventDimId = inventDim.InventDimId;

        if (firstTime)
        {
            setup.bomRouteDate  = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
        }

        if (!setup.bomRouteFromQty)
        {
            setup.bomRouteFromQty   = curInventTable.inventStandardQty(inventDim.InventDimId);
        }
    }

    if (!_caller.RecId)
    {
        return;
    }

    switch (_caller.TableId)
    {
        case (tablenum(BOMTable)):

            bomTable = _caller;

            element.pdsRefreshDimSetup(inventDim);
            inventDim.InventSiteId  = bomTable.SiteId;
            setupFromInventDim();

            break;

        case (tablenum(InventTable)):

            curInventTable    = _caller;

            inventDim.clear();
            inventDim.InventSiteId  = curInventTable.inventInventSiteId();
            element.pdsRefreshDimSetup(inventDim);

            setupFromInventDim();

            break;

        case (tablenum(InventDimCombination)):
            InventDimCombination inventDimCombination = _caller;
            curInventTable = inventDimCombination.inventTable();

            inventDim = inventDimCombination.inventDim().data();
            inventDim.InventSiteId  = curInventTable.inventInventSiteId('', inventDim.inventDimId);
            element.pdsRefreshDimSetup(inventDim);

            setupFromInventDim();

            break;

        case (tablenum(SalesLine)):

            salesLine      = _caller;
            curInventTable  = salesLine.inventTable();

            if (!curInventTable.isFormulaOrBOMAllowed())
            {
                break;
            }

            inventDim = salesLine.inventDim().data();
            inventDim.clearNotSiteAndProductDim(InventDimGroupSetup::newInventTable(curInventTable));
            element.pdsRefreshDimSetup(inventDim);
            inventDim = InventDim::findOrCreate(inventDim);

            setup.bomRouteInventDimId = inventDim.InventDimId;

            setup.bomRouteDate  = salesLine.ConfirmedDlv ? salesLine.ConfirmedDlv : DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
            setup.bomRouteFromQty = salesLine.QtyOrdered;

            break;

        case (tablenum(PurchLine)):

            purchLine      = _caller;
            curInventTable  = purchLine.inventTable();

            inventDim = purchLine.inventDim().data();
            inventDim.clearNotSiteAndProductDim(InventDimGroupSetup::newInventTable(curInventTable));
            element.pdsRefreshDimSetup(inventDim);
            inventDim = InventDim::findOrCreate(inventDim);
            setup.bomRouteInventDimId = inventDim.InventDimId;

            if (!curInventTable.isFormulaOrBOMAllowed())
            {
                break;
            }

            setup.bomRouteDate  = purchLine.ConfirmedDlv ? purchLine.ConfirmedDlv : DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
            setup.bomRouteFromQty = curInventTable.inventStandardQty(purchLine.inventDim().InventDimId);

            break;

        default:

            curInventTable = null;
            return;
    }

    setup_ds.write();
    setup_ds.refresh();

    inventDimSetup_DS.create();

    inventDimSetup.data(inventDim);
    inventDimSetup_DS.setCurrent();

    bomDesignerCtrl.linkActive(_caller,setup);
    element.setRoute(bomTree.getSelection(),true);
    element.setupChanged();
    element.updateDesign(InventDimFormDesignUpdate::Active);
    pmfFormCtrl_BOMDesigner.doLinkActivePost();

    firstTime = false;
}

public void setupChanged(boolean _updateFromBuffer = false)
{
    if (_updateFromBuffer)
    {
        setUp.write();
        bomDesignerCtrl.setupChange(setUp);
    }

    bomDesignerCtrl.controlFormatExplain(formatExplain_1);
    element.updateDesign(InventDimFormDesignUpdate::SetupChange);
}

void doRefresh()
{
    bomVersion_ds.reread();
    bomVersion_ds.refresh();
    bomVersion_ds.rereadReferenceDataSources();
}

public void init()
{
    if (!element.args().caller() || !element.args().dataset())
    {
        throw error(strfmt("@SYS22678",element.name()));
    }

    allowCreate = true;
    firstTime   = true;
    pmfFormCtrl_BOMDesigner = PmfFormCtrl_BOMDesigner::newFromForm(element);
    pmfFormCtrl_BomDesigner_BOMVersion = PmfFormCtrl_BomDesigner_BOMVersion::newFromForm(element);
    pmfFormCtrl_BOMDesigner.setDatasources(bom);
    pmfFormCtrl_BomDesigner_BOMVersion.setDatasources(bomVersion, inventDimBOMVersion);

    super();

    inViewMode = element.inViewMode();

    bomDesignerCtrl = this.bomDesignerCtrl();

    element.initCaption();
    element.initFilter();
    element.initLeftView();
    element.initRouteView();
    element.initBottomView();
    element.setWindowSize();
    element.setupChanged();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    pmfFormCtrl_BOMDesigner.initPost();
    pmfFormCtrl_BomDesigner_BOMVersion.initPost();
}

void initBottomView()
{
    bomDesignerCtrl.controlFormatExplain(formatExplain_1);
}

void initCaption()
{
    if (element.args().dataset() == tableNum(SalesLine)     ||
        element.args().dataset() == tableNum(PurchLine))
    {
        element.design().caption("@SYS26550");
    }
}

void updateCaption()
{
    if (element.args().dataset() != tableNum(SalesLine)
        && element.args().dataset() != tableNum(PurchLine))
    {
        str baseCaption = pmfFormCtrl_BOMDesigner.isFormulaEnabled() ? "@PRO1483" : "@SYS67085";
        InventDimParm inventDimParm = inventDimCtrlFrm.parmDimParmVisible();
        str caption = BomDesignerCtrl::generateCaption(InventDimSetup, inventDimParm, SetUp, baseCaption);
        element.design().caption(caption);
    }
}

