{
    bomTree.setImagelist(bomDesignerCtrl.imageList());
    bomTree.setStateImagelist(bomDesignerCtrl.imageListState());
}

void initRouteView()
{
    boolean routeVisible = bomDesignerCtrl.canViewRouteInfomation();
    RouteLinesTab.visible(routeVisible);

    if (routeVisible)
    {
        bomDesignerCtrl.initRouteView();
    }

    RouteTab.visible(routeVisible);
}

