}

void inventDimModified()
{
    InventDim inventDim = InventDim::findOrCreate(inventDimSetup);
    setup.bomRouteInventDimId = inventDim.InventDimId;

    element.setupChanged(true);

    inventDimSetup.data(inventDim);
    inventDimSetup_ds.setCurrent();

    this.updateCaption();
}

