void setButtonPanel()
{
    int         nodeIdx = bomTree.getSelection();
    int         rootIdx = bomTree.getRoot();
    boolean     isFormula = bomDesignerCtrl.pmfNode2BOMFormula(nodeIdx) == PmfBOMFormula::Formula;

    // BOM Lines menu and buttons
    boolean editBOMButtonEnabled;
    if (editBOMButton.visible())
    {
        editBOMButtonEnabled = !inViewMode && bomDesignerCtrl.canEditBOM(nodeIdx);
        if (editBOMButton.enabled() != editBOMButtonEnabled)
        {
            editBOMButton.enabled(editBOMButtonEnabled);
        }
    }

    boolean deleteBOMButtonEnabled;
    if (deleteBOMButton.visible())
    {
        deleteBOMButtonEnabled = !inViewMode && bomDesignerCtrl.canDeleteBOM(nodeIdx);
        if (deleteBOMButton.enabled() != deleteBOMButtonEnabled)
        {
            deleteBOMButton.enabled(deleteBOMButtonEnabled);
        }
    }

    boolean addLineBeforeAfterLineEnabled;
    if (AddLineAfterLine.visible())
    {
        addLineBeforeAfterLineEnabled = !inViewMode && bomDesignerCtrl.canEditBOM(nodeIdx) && (!bomDesignerCtrl.isPmfIdx(nodeIdx));
        AddLineAfterLine.enabled(addLineBeforeAfterLineEnabled);
        AddLineBeforeLine.enabled(addLineBeforeAfterLineEnabled);
    }

    boolean addLineAsComponentEnabled;
    if (AddLineAsComponent.visible())
    {
        addLineAsComponentEnabled = !inViewMode && bomDesignerCtrl.canAddComponent(nodeIdx);
        if (AddLineAsComponent.enabled() != addLineAsComponentEnabled)
        {
            AddLineAsComponent.enabled(addLineAsComponentEnabled);
        }
    }

    // BOM menu and buttons
    boolean createBOMVersionButtonEnabled;
    if (CreateBOMVersion.visible())
    {
        createBOMVersionButtonEnabled = !inViewMode && bomDesignerCtrl.canCreateBOMVersion(nodeIdx);
        if (CreateBOMVersion.enabled() != createBOMVersionButtonEnabled)
        {
            CreateBOMVersion.enabled(createBOMVersionButtonEnabled);
        }
    }

    boolean checkBOMButtonEnabled;
    if (checkBOMButton.visible())
    {
        checkBOMButtonEnabled = bomDesignerCtrl.canCheckBOM(nodeIdx);
        if (checkBOMButton.enabled() != checkBOMButtonEnabled)
        {
            checkBOMButton.enabled(checkBOMButtonEnabled);
        }
    }

    boolean printBOMButtonEnabled;
    if (printBOMButton.visible())
    {
        printBOMButtonEnabled = bomDesignerCtrl.canPrintBOM(nodeIdx);
        if (printBOMButton.enabled() != printBOMButtonEnabled)
        {
            printBOMButton.enabled(printBOMButtonEnabled);
        }
    }

    boolean calcBOMButtonEnabled;
    if (calcBOMButton.visible())
    {
        calcBOMButtonEnabled = bomDesignerCtrl.canOpenBOMCalc(nodeIdx);
        if (calcBOMButton.enabled() != calcBOMButtonEnabled)
        {
            calcBOMButton.enabled(calcBOMButtonEnabled);
        }
    }

    boolean bomVersButtonEnabled;
    if (bomVersButton.visible())
    {
        bomVersButtonEnabled = bomDesignerCtrl.canOpenBOMVers(nodeIdx);
        if (bomVersButton.enabled() != bomVersButtonEnabled)
        {
            bomVersButton.enabled(bomVersButtonEnabled);
        }

        if (isFormula)
        {
            BOMVersButton.text("@PRO86");
        }
        else
        {
            BOMVersButton.text("@SYS22799");
        }
    }

    // Route menu and buttons
    boolean routeVersButtonEnabled;
    if (routeVersButton.visible())
    {
        routeVersButtonEnabled = bomDesignerCtrl.canOpenRouteVers(nodeIdx);
        if (routeVersButton.enabled() != routeVersButtonEnabled)
        {
            routeVersButton.enabled(routeVersButtonEnabled);
        }
    }

    boolean routeLockButtonEnabled;
    if (routeLockButton.visible())
    {
        routeLockButtonEnabled = bomDesignerCtrl.canLockRoute(nodeIdx);
        if (routeLockButton.enabled() != routeLockButtonEnabled)
        {
            routeLockButton.enabled(routeLockButtonEnabled);
        }
    }

    boolean routeReleaseButtonEnabled;
    if (routeReleaseButton.visible())
    {
        routeReleaseButtonEnabled = bomDesignerCtrl.canReleaseRoute();
        if (routeReleaseButton.enabled() != routeReleaseButtonEnabled)
        {
            routeReleaseButton.enabled(routeReleaseButtonEnabled);
        }
    }

    // Configuration menu and buttons
    boolean configurationMaintainEnabled;
    if (configMaintainButton.visible())
    {
        configurationMaintainEnabled = bomDesignerCtrl.canOpenConfigTable(nodeIdx);
        if (configMaintainButton.enabled() != configurationMaintainEnabled)
        {
            configMaintainButton.enabled(configurationMaintainEnabled);
        }
    }

    boolean configurationViewEnabled;
    if (configWhereUsedButton.visible())
    {
        configurationViewEnabled = bomDesignerCtrl.canOpenConfigPartOf(nodeIdx);
        if (configWhereUsedButton.enabled() != configurationViewEnabled)
        {
            configWhereUsedButton.enabled(configurationViewEnabled);
        }
    }
}

    void setRoute(int _curBOMNode = curBOMNode, boolean _forced = false)
    {
        if (!bomDesignerCtrl.mustUpdateDelayedInfo() && !_forced)
        {
            return;
        }

        element.setButtonPanel();

        if (bomDesignerCtrl.canViewRouteInfomation())
        {
            bomDesignerCtrl.setRoute(_curBOMNode);
        }

        if (!bomDesignerCtrl.isPmfIdx(_curBOMNode))
        {
            bomDesignerCtrl.setBOMInfo(_curBOMNode);
        }

        // Refresh form data sources if needed
        str newItemQueryValue = queryValue(bomDesignerCtrl.itemId(_curBOMNode));
        if (_forced || newItemQueryValue != selectedItemRange.value())
        {
            selectedItemRange.value(newItemQueryValue);
            InventTableSelected_ds.executeQuery();

			// If a formula designer, after the sub-item selected, the formula version should be updated.            
            if (this.pmfIsEnabled() && InventTable::exist(newItemQueryValue))
            {
                select bomVersion where bomVersion.ItemId == newItemQueryValue;                

                if (inventDimBomVersion.inventDimId != bomVersion.InventDimId)
                {
                    inventDimBOMVersion.data(InventDim::find(bomVersion.InventDimId));
                    inventDimBOMVersion_ds.setCurrent();
                }
                pmfFormCtrl_BomDesigner_BOMVersion.dataSourceInitPost(bomVersion);
			}
        }

        str newBOMRecIdQueryValue = queryValue(bomDesignerCtrl.currentBomTable().RecId);
        if (_forced || newBOMRecIdQueryValue != selectedBOMTableRecIdRange.value())
        {
            selectedBOMTableRecIdRange.value(newBOMRecIdQueryValue);
            BOMTableSelected_DS.executeQuery();
        }

        str newRouteRecIdQueryValue = queryValue(bomDesignerCtrl.currentRouteTable().RecId);
        if (_forced || newRouteRecIdQueryValue != selectedRouteTableRecIdRange.value())
        {
            selectedRouteTableRecIdRange.value(newRouteRecIdQueryValue);
            RouteTableSelected_DS.executeQuery();
        }

        str newBOMLineRecIdQueryValue = queryValue(bomDesignerCtrl.bomRecord(_curBOMNode).RecId);
        if (_forced || newBOMLineRecIdQueryValue != selectedBOMLineRecIdRange.value())
        {
            selectedBOMLineRecIdRange.value(newBOMLineRecIdQueryValue);
            BOM_DS.executeQuery();
        }
    }

