    }
}

/// <summary>
/// Capture the task when edit the BOM designer.
/// </summary>
/// <param name="_taskId">
/// The task id.
/// </param>
/// <returns>
/// Returns 1 if succeeded; otherwise 0.
/// </returns>
public int task(int _taskId)
{
    #Task
    int ret;

    ret = super(_taskId);

    switch (_taskId)
    {
        case #taskEditRecord :
            inViewMode = element.inViewMode();
            this.setRoute(curBOMNode, true);
            break;
    }
    return ret;
}

void updateDesign(
    InventDimFormDesignUpdate   _mode,
    InventTable                 _inventTable = null
    )
{
    InventTable     inventTableParameter = _inventTable;
    InventDimParm   inventDimParmShowBomVersionGrid;
    boolean         canCreate = (bomDesignerCtrl.selectedBOMId() != '');
    InventDimParm   inventDimParmShow;

    if (inventTableParameter == null)
    {
        inventTableParameter = curInventTable;
    }

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (! inventDimCtrlFrm)
            {
                inventDimCtrlFrm = InventDimCtrl::newFromFormValidDataSource(element,  InventDimSetup_DS.name());
            }
            if (#PmfEnabled)
            {
                if (!inventDimFormSetupBOMVersion)
                {
                    inventDimFormSetupBOMVersion = InventDimCtrl_Frm_BOMDesignerBOMVersion::newFromFormAndValidDataSources(element, InventDimBomVersion_DS.name());
                }
            }

        case InventDimFormDesignUpdate::Active,
                InventDimFormDesignUpdate::LinkActive,
                InventDimFormDesignUpdate::FieldChange:

            inventDimParmShow.initDimBOMRouteVersion(InventDimGroupSetup::newInventTable(curInventTable));
            inventDimCtrlFrm.parmDimParmVisible(inventDimParmShow);
            inventDimCtrlFrm.parmDimParmEnabled(inventDimParmShow);
            inventDimCtrlFrm.formSetControls(true);

            if (#PmfEnabled && inventTableParameter)
            {
                inventDimParmShowBomVersionGrid.initDimBOMRouteVersion(InventDimGroupSetup::newInventTable(inventTableParameter));
                inventDimFormSetupBOMVersion.parmDimParmVisibleGrid(inventDimParmShowBomVersionGrid);
                inventDimFormSetupBOMVersion.parmDimParmVisible(inventDimParmShowBomVersionGrid);
                inventDimFormSetupBOMVersion.parmDimParmEnabled(inventDimParmShowBomVersionGrid);
                inventDimFormSetupBOMVersion.formSetControls(true);
            }

            this.updateCaption();

            break;

        case InventDimFormDesignUpdate::SetupChange:
            //jump out
            break;
        default :
            throw error(strfmt("@SYS54195",funcname()));
    }

    canCreate =  #PMFEnabled && canCreate && pmfFormCtrl_BomDesigner_BOMVersion.isFormulaOnly();

    BOMVersion_DS.allowDelete(canCreate);
    BOMVersion_DS.allowCreate(canCreate);
    BOMVersion_DS.allowEdit(canCreate);
}

	/// <summary>
    /// Re-expands the BOMTree element after adding the first element to the tree.
    /// </summary>
    /// <remarks>
    /// This is needed because of the way how platform is handling tree controls without elements.
	/// </remarks>
	private void reExpandBOMTreeAfterAddingFirstElementToRoot()
    {
        int rootId = bomTree.getRoot();

        bomTree.expand(rootId, FormTreeExpand::Collapse);
        bomTree.expand(rootId, FormTreeExpand::Expand);

        int newElementId = bomTree.getChild(rootId);

        bomTree.select(newElementId);
    }

private void setControlDecimals()
{
    if (this.pmfIsEnabled())
    {
        BOM_BOMQtySerie.noOfDecimals(UnitOfMeasure::findBySymbol(bomVersion.pmfFormulaUnit()).DecimalPrecision);
    }
}

