public void linkActive()
{
    super();
    if (element.args().dataset() == tablenum(InventTable)   ||
        element.args().dataset() == tablenum(SalesLine)     ||
        element.args().dataset() == tablenum(PurchLine)     ||
        element.args().dataset() == tablenum(InventDimCombination))
    {
        element.doLinkActive(element.args().record());
    }
}

