{
    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceCreatePre();
    super(_append);
}

public boolean validateWrite()
{
    boolean ret;

    bomVersion.InventDimId  = InventDim::findOrCreate(inventDimBOMVersion).InventDimId;
    if (!bomVersion.BOMId)
    {
        BOMVersion.BOMId = bomDesignerCtrl.selectedBOMId();
    }

    ret = super();

    return ret;
}

public int active()
{
    int     ret;

    ret = super();

    pmfFormCtrl_BomDesigner_BOMVersion.parmBOM(bom);
    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceActivePost();

    if (#PmfEnabled)
    {
        bomDesignerCtrl.parmBOMVersionCaller(bomVersion);
    }

    element.setControlDecimals();
    element.updateDesign(InventDimFormDesignUpdate::Active,bomVersion.inventTable());

    return ret;
}

public void init()
{
    super();

    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceInitPost(bomVersion);
    bomVersion_ds.cacheAddMethod(tablemethodstr(BOMVersion, pdsCWUnitId));
}

public void write()
{
    InventDim   inventDim;

    ttsbegin;
    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceWritePre();

    inventDim = InventDim::findOrCreate(inventDimBomVersion);
    bomVersion.InventDimId = inventDim.InventDimId;

    super();

    if (inventDimBomVersion.InventDimId != bomVersion.InventDimId)
    {
        inventDimBOMVersion.data(inventDim);
        inventDimBOMVersion_ds.setCurrent();
    }

    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceWritePost();

    ttscommit;
}

public void lookup(FormControl _formControl, str _filterStr = "")
{
