public void init()
{
    super();

    if (#PMFEnabled)
    {
        BOM_DS.allowDelete(NoYes::Yes);
        BOM_DS.allowCreate(NoYes::Yes);
        BOM_DS.allowEdit(NoYes::Yes);
    }

    QueryBuildDataSource qbds = this.queryBuildDataSource();
    qbds.clearLinks();
    qbds.clearDynalinks();
    selectedBOMLineRecIdRange = qbds.addRange(fieldnum(BOM, RecId));
    selectedBOMLineRecIdRange.value(queryValue(0));
}

/// <summary>
/// Refreshes the data contained in the data source and corrects the data on the linked data sources.
/// </summary>
/// <param name="_retainPosition">
/// A Boolean value that specifies if the data source should preserve its current record selection.
/// </param>
/// <remarks>
/// The research methods are called automatically when the form is refreshed.
/// This method call the doLinkActive method on the element that corrects and refreshes
/// the data on the InventDimSetup and SetUp data sources.
/// </remarks>
public void research(boolean _retainPosition = false)
{
    super(_retainPosition);
    this.linkActive();
}

public void linkActive()
{
    super();

    if (element.args().dataset() == tablenum(BOMTable))
    {
        element.doLinkActive(element.args().record());
    }
}

public void write()
{
    pmfFormCtrl_BomDesigner_BOMVersion.parmBOM(bom);
    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceWritePre();

    super();

    pmfFormCtrl_BomDesigner_BOMVersion.dataSourceWritePost();
}

