/// <summary>
/// Gets or sets if the route operation is related to the current selected BOM line.
/// </summary>
/// <param name = "_set">Determines if the value is to be read or set.</param>
/// <param name = "_route">The route operation.</param>
/// <param name = "_usedInBOM">The new value set by the user.</param>
/// <returns>True if this route operation is related to the BOM line selected in the tree.</returns>
public edit BOMDesignerOprUsedInBOM usedInBOMLine(boolean _set, Route _route, BOMDesignerOprUsedInBOM _usedInBOM)
{
    if (_set)
    {
        bomDesignerCtrl.setOperationReleatedToBOMLine(_route, BOMTree.getSelection(), _usedInBOM);

        this.research(true);
        BOM_DS.research(true);

        return _usedInBOM;
    }
    else
    {
        return bomDesignerCtrl.isOperationReleatedToBOMLine(_route, BOMTree.getSelection());
    }
}

public void clicked()
{
    int idx = bomTree.getSelection();
    if (bomDesignerCtrl.canEditBOM(idx))
    {
        bomDesignerCtrl.openBOMFormNodeIdx(idx);
    }
}

public void clicked()
{
    int idx = bomTree.getSelection();
    if (bomDesignerCtrl.canDeleteBOM(idx))
    {
        if (bomDesignerCtrl.isPmfIdx(idx))
        {
            bomDesignerCtrl.pmfRemoveCoByLine(idx);
        }
        else
        {
            bomDesignerCtrl.removeLine(idx);
        }
    }
}

public void clicked()
{
    bomDesignerCtrl.addLine(curBOMNode, false, false);
}

        public void clicked()
        {
            int rootId				= bomTree.getRoot();
            int existingRootChildId	= bomTree.getChild(rootId);

            boolean isNewElementAdded = bomDesignerCtrl.addLine(curBOMNode, false, true);

            if (isNewElementAdded
				&& !existingRootChildId)
            {
                element.reExpandBOMTreeAfterAddingFirstElementToRoot();
            }
        }

public void clicked()
{
    bomDesignerCtrl.addLine(curBOMNode, true, false);
}

public void clicked()
{
    boolean created = bomDesignerCtrl.createNewBOMVersion(curBOMNode);
    if (created && SetUp.BOMRouteVersionPrinciple == BOMRouteVersionSelect::Active)
    {
        SetUp.BOMRouteVersionPrinciple = BOMRouteVersionSelect::SelectedActive;
        element.inventDimModified();
    }
}

