public void clicked()
{
    // This needs to be handled with custom code since there is no standard way of parsing an object to a menu function in meta data
    Args args = new Args();
    args.parmObject(bomDesignerCtrl);
    args.caller(element);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(BOMDesignerSetup), MenuItemType::Display);
    FormRun formRun = menuFunction.create(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        buf2Buf(bomDesignerCtrl.parmTmpBOMRouteTree(), setUp);
        element.setupChanged(true);
    }
}

public void clicked()
{
    // This needs to be handled with custom code since there is no standard way of parsing an object to a menu function in meta data
    var dialogFilter = BOMDesignerFilterDialogParms::newParms(curInventTable, InventDimSetup, SetUp);
    Args args = new Args();
    args.parmObject(dialogFilter);
    args.caller(element);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(BOMDesignerFilterDialog), MenuItemType::Display);
    FormRun formRun = menuFunction.create(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        buf2Buf(dialogFilter.parmTmpBOMRouteTree(), setUp);
        element.setupChanged(true);
        buf2Buf(dialogFilter.parmInventDimSetup(), InventDimSetup);
        element.inventDimModified();
    }
}

public boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    boolean ret;

    if (_action == FormTreeExpand::Expand)
    {
        bomDesignerCtrl.expandNode(_idx);
    }

    ret = super(_idx, _action, _data);

    return ret;
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if (_newItem.idx() != _oldItem.idx())
    {
        curBOMNode = _newItem.idx();

        if (!bomDesignerCtrl.isLoading())
        {
            element.setRoute(curBOMNode, true);
        }
    }
}
