[Form]
public final class BOMDesignerBOMVersion extends FormRun
{
    #ResAppl

    BOMDesignerCtrl             bomDesignerCtrl;
    ItemId                      curItemId;
    int                         callerNodeIdx;
    QueryBuildRange             criteriaVersionItemId;
    BOMId                       lastBOMId;

    InventDimCtrl_Frm           inventDimFormSetupBOMVersion;
    PmfFormCtrl_BOMDesignerBOMVersion   pmfFormCtrl_BOMDesignerBOMVersion;
}

void checkBOM(BOMId bomId)
{
    SysOperationProgress waitDialog;
    if (!bomId || !BOMTable::find(bomId).CheckBOM)
        return;

    waitDialog = BOMHierarchyCheck::showWaitDialog();

    BOMHierarchyCheck::checkBOM(bomId,!BOMParameters::find().mustCheckCircularityManual());
    waitDialog.hide();
}

void close()
{
    super();

    if (bomVersion.bomId && bomVersion.bomId != naStr())
        element.checkBOM(bomVersion.bomId);
}

void doRefresh()
{
    bomVersion_ds.reread();
    bomVersion_ds.refresh();
    bomVersion_ds.rereadReferenceDataSources();
}

void doResearch(boolean doBOM = true)
{
    if (!doBOM)
        bomVersion_ds.research();
}

void init()
{
    if (!element.args() || !element.args().caller() || classidget(element.args().caller()) != classnum(BOMDesignerCtrl))
    {
        super();
        element.close();
        return;
    }

    bomDesignerCtrl     = element.args().caller();
    callerNodeIdx       = str2int(element.args().parm());

    curItemId           = bomDesignerCtrl.itemId(callerNodeIdx);

    if (!bomDesignerCtrl || !curItemId)
    {
        super();
        element.close();
        return;
    }
    pmfFormCtrl_BOMDesignerBOMVersion
        = PmfFormCtrl_BOMDesignerBOMVersion::newFromForm(element);
    pmfFormCtrl_BOMDesignerBOMVersion.parmItemId(curItemId);
    super();

    element.setBOMVersionApprove();
    element.updateDesign(InventDimFormDesignUpdate::Init,InventTable::find(curItemId));

    pmfFormCtrl_BOMDesignerBOMVersion.setDatasources(bomVersion);
    pmfFormCtrl_BOMDesignerBOMVersion.initPost();
}

public void run()
{
    pmfFormCtrl_BOMDesignerBOMVersion.runPre();
    super();
    element.setEnabledButtons();
}

void setBOMVersionApprove()
{
    bomVersionApprove.enabled((bomVersion.RecId != 0 || bomVersion.bomId));
}

void setEnabledButtons()
{
    if (!selectVersion.visible() && !deSelectVersion.visible())
        return;

    if (!bomVersion.RecId)
    {
        selectVersion.enabled(false);
        deSelectVersion.enabled(false);
        return;
    }
    selectVersion.enabled(bomVersion.Construction == NoYes::No);
    deSelectVersion.enabled(bomVersion.Construction == NoYes::Yes);
}

void updateBOMDesigner()
{
    bomDesignerCtrl.resetBOM(curItemId);
}

void updateDesign(
    InventDimFormDesignUpdate       _mode,
    InventTable                     _inventTable)
{
    InventDimParm   inventDimParmVisible;
    InventDimParm   inventDimParmEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetupBOMVersion)
            {
                inventDimFormSetupBOMVersion = InventDimCtrl::newFromForm(element);
            }
            inventDimParmVisible.initDimBOMRouteVersion(InventDimGroupSetup::newInventTable(_inventTable));
            inventDimFormSetupBOMVersion.parmDimParmVisibleGrid(inventDimParmVisible);

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange :

            inventDimParmEnabled.initDimBOMVersionEnable(InventDimGroupSetup::newInventTable(_inventTable),bomVersion);
            inventDimFormSetupBOMVersion.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetupBOMVersion.formSetControls(true);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

private void setControlDecimals()
{
    if (pmfFormCtrl_BOMDesignerBOMVersion.isFormulaEnabled())
    {
        BOMVersion_PmfBatchSize.noOfDecimals(UnitOfMeasure::findBySymbol(bomVersion.pmfFormulaUnit()).DecimalPrecision);
    }
}

/// <summary>
/// Selects the current <c>BOMVersion</c>.
/// </summary>
/// <remarks>This is a helper function for <c>delete</c>, <c>write</c>, and <c>executeQuery</c>.</remarks>
/// <returns>The current version</returns>
private BOMVersion selectVersion()
{
    BOMVersion curVersion;

    if (bomDesignerCtrl.bomRouteVersionPrinciple() == BOMRouteVersionSelect::Active)
    {
        curVersion = BOMVersion::findActive(curItemId, bomDesignerCtrl.versionDate(), bomDesignerCtrl.versionQty(), bomDesignerCtrl.inventDim(callerNodeIdx));
    }
    else
    {
        select firstonly curVersion where curVersion.ItemId == curItemId && curVersion.Construction == NoYes::Yes;
        if (bomDesignerCtrl.bomRouteVersionPrinciple() == BOMRouteVersionSelect::SelectedActive && !curVersion.RecId)
        {
            curVersion = BOMVersion::findActive(curItemId, bomDesignerCtrl.versionDate(), bomDesignerCtrl.versionQty(), bomDesignerCtrl.inventDim(callerNodeIdx));
        }
    }

    return curVersion;
}

public void delete()
{
    BOMVersion curVersionBefore = this.selectVersion();

    super();

    BOMVersion curVersionAfter = this.selectVersion();

    if (curVersionBefore != curVersionAfter)
        element.updateBOMDesigner();
}

public void write()
{
    BOMVersion curVersionBefore = this.selectVersion();

    InventDim inventDim = InventDim::findOrCreate(inventDimBOMVersion);
    bomVersion.InventDimId = inventDim.InventDimId;
    pmfFormCtrl_BOMDesignerBOMVersion.dataSourceWritePre();

    super();

    BOMVersion curVersionAfter = this.selectVersion();

    if (curVersionBefore != curVersionAfter)
        element.updateBOMDesigner();

    if (inventDimBOMVersion.InventDimId != bomVersion.InventDimId)
    {
        inventDimBOMVersion.data(inventDim);
        inventDimBOMVersion_ds.setCurrent();
    }
}

public boolean validateWrite()
{
    boolean ret;
    bomVersion.InventDimId = InventDim::findOrCreate(inventDimBOMVersion).InventDimId;

    ret = super();

    ret = ret && pmfFormCtrl_BOMDesignerBOMVersion.dataSourceValidateWrite();
    return ret;
}

public void executeQuery()
{
    super();

    BOMVersion curVersion = this.selectVersion();

    if (curVersion)
        bomVersion_ds.positionToRecord(curVersion);
}

void linkActive()
{
    // No active link
}

void init()
{
    Query                   q = new Query();
    QueryBuildDataSource    qB;

    super();

    qB = q.addDataSource(tablenum(BOMVersion));
    qB.addSortIndex(indexnum(BOMVersion,ItemFromToIdx));
    qB.addRange(fieldnum(BOMVersion,ItemId)).value(queryValue(curItemId));
    this.query(q);
    pmfFormCtrl_BOMDesignerBOMVersion.dataSourceInitPost(bomVersion);
}

int active()
{
    int ret;

    if (lastBOMId && lastBOMId != bomVersion.bomId)
        element.checkBOM(lastBOMId);

    ret = super();

    pmfFormCtrl_BOMDesignerBOMVersion.dataSourceActivePost();
    lastBOMId = bomVersion.bomId;
    element.setBOMVersionApprove();
    element.setControlDecimals();
    element.setEnabledButtons();

    element.updateDesign(InventDimFormDesignUpdate::Active, bomVersion.inventTable());
    return ret;
}

public void create(boolean _append = false)
{
    pmfFormCtrl_BOMDesignerBOMVersion.dataSourceCreatePre();

    super(_append);
}

void modified()
{
    super();

    element.setBOMVersionApprove();

    if (bomVersion.bomId && ! bomVersion.Name)
        bomVersion.Name = bomVersion.bomTable().Name;

    element.updateDesign(InventDimFormDesignUpdate::FieldChange, bomVersion.inventTable());
}

