void clicked()
{
    BOMId           fromId;
    PmfBatchSize    fromBatchSize;
    FromQty         fromQty;
    PdsCWInventQty  fromCWQty;
    boolean         dialogCopy;

    InventSiteId    fromSiteId;
    InventDim       inventDim;

    super();

    if (bomVersion)
    {
        fromId      = bomVersion.bomId;
        fromSiteId  = bomVersion.inventSiteId();

        if (bomVersion.PmfBatchSize)
        {
            fromBatchSize = bomVersion.PmfBatchSize;
        }
        else
        {
            fromBatchSize = 1;
        }

        if (bomVersion.FromQty)
        {
            fromQty = bomVersion.FromQty;
        }

        if (PdsGlobal::pdsIsCWItem(bomVersion.ItemId))
        {
            if (bomVersion.PdsCWSize)
            {
                fromCWQty = bomVersion.PdsCWSize;
            }
            else
            {
                fromCWQty = 1;
            }
        }
    }

    if (bomVersion || ! bomVersion.ItemId)
        bomVersion_ds.create();

    [bomVersion.bomId, inventDim.InventSiteId
        , dialogCopy
        ]
        = BOMCreate::newBOMCreateDialog().promptCreateBOM(bomVersion.ItemId, fromId, fromSiteId
            , pmfFormCtrl_BOMDesignerBOMVersion.isFormulaEnabled()
            );

    if (#PmfEnabled && bomVersion.bomId)
    {
        bomVersion.Name = BOMTable::find(bomVersion.bomId).Name;
        bomVersion.PmfBatchSize = fromBatchSize == 0 || dialogCopy
                                        ? fromBatchSize
                                        : 1;
        bomVersion.FromQty      = fromQty == 0 || dialogCopy
                                        ? fromQty
                                        : 1;

        if (PdsGlobal::pdsIsCWItem(bomVersion.ItemId))
        {
            bomVersion.PdsCWSize = fromCWQty == 0 || dialogCopy
                                        ? fromCWQty
                                        : 0;
        }
    }

    if (bomVersion.bomId)
    {
        bomVersion.initFromBOMTable(bomVersion.bomTable());
        bomVersion.Name = BOMTable::find(bomVersion.bomId).Name;
        bomVersion_ds.write();
        bomVersion_ds.refresh();
    }
    else
    {
        bomVersion_ds.delete();
    }
    element.setBOMVersionApprove();
}

public void clicked()
{
    super();
    element.doRefresh();
}

void clicked()
{
    BOMVersion bomVersionLocal;

    if (   !bomVersion.RecId
        ||  bomVersion.Construction == NoYes::Yes)
    {
        return;
    }

    bomversion.setSelected();

    if (bomDesignerCtrl.bomRouteVersionPrinciple() != BOMRouteVersionSelect::Active)
    {
        element.updateBOMDesigner();
    }

    bomVersion_ds.research(true);
}

void clicked()
{
    BOMVersion bomVersionLocal;

    if (!bomVersion.RecId)
        return;

    if (bomVersion.Construction == NoYes::Yes)
    {
        ttsBegin;
        bomVersionLocal = BOMVersion::findRecId(BOMVersion.RecId, true);
        bomVersionLocal.Construction = NoYes::No;
        bomVersionLocal.doUpdate();
        ttsCommit;
    }

    if (bomDesignerCtrl.bomRouteVersionPrinciple() != BOMRouteVersionSelect::Active)
    {
        element.updateBOMDesigner();
    }

    bomVersion_ds.research(true);
}

void lookup()
{
    if (bomVersion.ItemId)
        BOMTable::lookupBOMId(this, InventTable::find(bomVersion.ItemId).itemGroupId(), bomVersion.inventSiteId());
    else
        super();
}

void clicked()
{
    super();

    if (this.value() == NoYes::Yes && !bomVersion_ds.validateWrite())
    {
        bomVersion.Active = NoYes::No;
        this.value(NoYes::No);
    }
    else
        bomVersion_ds.write();
}
