[Form]
public class BOMDesignerEditBOM extends FormRun
{
    BOMDesignerCtrl     bomDesignerCtrl;
    boolean             mayWrite;
    boolean             allowCreate;
    boolean             createMode;
    BOM                 curBOM;
    InventDimCtrl_Frm   inventDimFormSetup;
    PmfFormCtrl_BOMDesignerEditBOM  pmfFormCtrl_BOMDesignerEditBOM;
    private boolean     isWarehouseProductionRawMaterialOverpickingFlightEnabled = WHSProductionRawMaterialOverpickingFlight::instance().isEnabled();

}

BOMDesignerCtrl bomDesignerCtrl()
{
    return bomDesignerCtrl;
}

public boolean canClose()
{
    boolean ret = true;

    if (element.closedOk())
    {
        bom.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
        if (!bom_ds.validateWrite(true))
        {
            ret =  false;
        }
    }
    ret = ret && super();

    return ret;
}

public void close()
{
    bom.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    // Update if create mode
    if (element.closedOk() && bom_ds.validateWrite(true))
    {
        mayWrite = true;
        bom_ds.write();

        // Ensure that original caller record gets updated with the created record
        curBOM.data(BOM);

        if (bomDesignerCtrl)
        {
            if (!createMode)
            {
                bomDesignerCtrl.updateBOM(bom);
            }
        }
    }

    super();
}

void enableFields()
{
    boolean isProducedByDefault = bom.inventTable().isProducedByDefault();

    bom_ds.object(fieldnum(BOM,ItemBOMId)).allowEdit(isProducedByDefault);
    bom_ds.object(fieldnum(BOM,ItemRouteId)).allowEdit(isProducedByDefault);

    if (bom.WrkCtrConsumption || !inventDim.InventSiteId)
        inventDim_ds.object(fieldnum(InventDim,InventLocationId)).enabled(false);
    else
        inventDim_ds.object(fieldnum(InventDim,InventLocationId)).enabled(true);

    element.enableWarehouseProcessFields();
}

private void enableWarehouseProcessFields()
{
    WarehouseProcesses_OverrideMaterialOverpick.visible(isWarehouseProductionRawMaterialOverpickingFlightEnabled);

    if (isWarehouseProductionRawMaterialOverpickingFlightEnabled)
    {
        boolean isSettingDefault = this.isOverrideMaterialOverpickSettingDefault();

        WarehouseProcesses_OverrideMaterialOverpick.value(!isSettingDefault);
        bom_ds.object(fieldnum(BOM, MaterialOverpickPercentage)).allowEdit(!isSettingDefault);
    }
}

private boolean isOverrideMaterialOverpickSettingDefault()
{
    return (WarehouseProcesses_MaterialOverpickPercentage.realValue() == BOM.whsInventTable().MaterialOverpickPercentage);
}

public void init()
{
    if (!element.args() || !element.args().caller())
    {
        super();
        element.close();
        return;
    }

    Object caller = element.args().caller();

    if (caller is BOMDesignerCtrl)
    {
        bomDesignerCtrl     = caller;
    }

    curBOM = element.args().record();

    if (!curBOM.RecId)
    {
        createMode = true;
    }

    pmfFormCtrl_BOMDesignerEditBOM = PmfFormCtrl_BOMDesignerEditBOM::newFromForm(element);

    super();

    pmfFormCtrl_BOMDesignerEditBOM.setDatasources(bom);
    pmfFormCtrl_BOMDesignerEditBOM.initPost();

    if (caller is FormRun && caller.name() == formStr(BOMConsistOf))
    {
        FormDataSource dataSourceVar = caller.dataSource(formDataSourceStr(BOMConsistOf, BOMVersion));
        BOMVersion bomVersion = dataSourceVar.cursor();
        pmfFormCtrl_BOMDesignerEditBOM.parmBOMVersion(bomVersion);
    }

    if (createMode)
    {
        allowCreate = true;
        bom_ds.create();
        bom.data(curBOM);
        inventDim.data(bom.inventDim());
        bom_ds.write();
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void run()
{
    pmfFormCtrl_BOMDesignerEditBOM.runPre();
    super();
    if (createMode && bomDesignerCtrl && bomDesignerCtrl.parmTmpBOMRouteTree().AutoEdit)
    {
        bomQty.setFocus();
    }
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm               inventDimParmEnabled;
    InventDimGroupSetup         inventDimGroupSetup;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }
            inventDimFormSetup.parmSkipOnHandLookUp(true);

        case InventDimFormDesignUpdate::Active      :
        case InventDimFormDesignUpdate::FieldChange :
            inventDimGroupSetup = InventDimGroupSetup::newItemId(bom.ItemId);
            inventDimFormSetup.formActiveSetup(inventDimGroupSetup);
            inventDimParmEnabled.initDimBOMEnable(inventDimGroupSetup, bom);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

private void setControlDecimals()
{
    BOMVersion bomVersion;

    if (curBOM && pmfFormCtrl_BOMDesignerEditBOM.isFormulaEnabled())
    {
        select firstonly bomVersion
            where bomVersion.bomId == curBOM.BOMId;

        if (bomVersion)
        {
            BOMQtySerie.noOfDecimals(UnitOfMeasure::findBySymbol(bomVersion.pmfFormulaUnit()).DecimalPrecision);
        }
    }
}

public boolean validateWrite(boolean _doIt = false)
{
    boolean ret;

    if (!_doIt)
        return true;

    bom.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    ret = ret && pmfFormCtrl_BOMDesignerEditBOM.dataSourceValidateWrite();
    return ret;
}

public void executeQuery()
{
    if (!createMode)
        super();
}

void create(boolean append = false)
{
    if (allowCreate)
    {
        pmfFormCtrl_BOMDesignerEditBOM.dataSourceCreatePre();
        super(append);
    }
}

public int active()
{
    int     ret;
    boolean isProducedByDefault = bom.inventTable().isProducedByDefault();

    ret = super();

    pmfFormCtrl_BOMDesignerEditBOM.dataSourceActivePost();
    element.enableFields();
    element.updateDesign(InventDimFormDesignUpdate::Active);
    element.setControlDecimals();

    itemBOMId.allowEdit(isProducedByDefault);
    itemRouteId.allowEdit(isProducedByDefault);

    return ret;
}

public void write()
{
    if (mayWrite)
    {
        mayWrite = false;
        bom.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
        pmfFormCtrl_BOMDesignerEditBOM.dataSourceWritePre();
        super();
        pmfFormCtrl_BOMDesignerEditBOM.dataSourceWritePost();
    }
}

public void init()
{
    super();
    this.query().dataSourceNo(1).addRange(fieldnum(BOM,RecId)).value(queryValue(curBOM.RecId));
    pmfFormCtrl_BOMDesignerEditBOM.dataSourceInitPost(bom);
}

public void jumpRef()
{
    BOMTable::jumpRefBomTable(BOMTable::find(bom.bomId),this);
}

