public boolean validate()
{
    boolean ret;

    if (inventDim.ConfigId && bom.ConfigGroupId)
        return checkFailed("@SYS70191");

    ret = super();

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    InventTable::lookupBOMId(_formControl, bom.ItemId, inventDim);
}

public void modified()
{
    // When changing item the product dimensions must be cleared in order to not get invalid combinations
    inventDim.clearProductDimensions();
    InventDim.clearStorageDimensions();
    bom.InventDimId = InventDim::findOrCreate(InventDim).inventDimId;

    super();

    bom.ConfigGroupId = '';

    element.enableFields();
    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    bom_ds.refresh();
    inventDim_ds.refresh();
    pmfFormCtrl_BOMDesignerEditBOM.fieldModified(fieldnum(BOM,ItemId));
}

public void lookup(FormControl _formControl, str _filterStr)
{
    InventTable::lookupRouteId(_formControl, bom.ItemId, inventDim);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    FormIntControl  oprNumControl = _formControl as FormIntControl;
    if (oprNumControl
       && element.args().caller()
       && element.args().caller().name() == formstr(BOMConsistOf))
    {
        Args args = new Args(formStr(RouteLookupOprNum));
        args.record(bom);
        args.caller(element.args().caller());

        FormRun formRun = classFactory.formRunClass(args);
        formRun.init();

        oprNumControl.performFormLookup(formRun);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

