[Form]
public class BOMExpandPurch extends FormRun
{
    #resAppl

    PurchLine             callerPurchLine;
    FormDataSource        callerDataSource;

    TmpFrmVirtual         tmpFrmVirtual;
    BOM                   tmpBOM;

    QueryBuildRange       queryRecId;   

    InventDimCtrl_Frm     inventDimFormSetupBOM;
    InventDimCtrl_Frm     inventDimFormSetupPurchLine;

}

boolean  canClose()
{
    if (element.closedOk())
    {
        tmpFrmVirtual.initFromBomDS(BOM_DS);

       if (tmpFrmVirtual
            && callerDataSource.allowEdit()
            && (purchLine.isOpenOrder())
            && purchLine.purchTable().checkUpdate())
        {
            try
            {
                PurchLine::findRecId(purchLine.RecId, true).expandBOM(tmpFrmVirtual, tmpBOM);
            }
            catch (Exception::Error)
            {
                return false;
            }
            FormDataUtil::getFormDataSource(purchLine).reread();
            FormDataUtil::getFormDataSource(purchLine).refresh();
        }
    }

    return super();
}

void  init()
{
    if (! element.args().caller() || ! element.args().record())
    {
        throw error("@SYS22539");
    }

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm dimParmVisibleBOM, dimParmEnabledBOM, dimParmVisiblePurchLine, dimParmEnabledPurchLine;
    Set           validBOM, validPurchLine;

    validBOM        = new Set(Types::String);
    validPurchLine  = new Set(Types::String);

    validBOM.add(inventDimBOM_ds.name());
    validPurchLine.add(inventDimPurchLine_ds.name());

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init,
             InventDimFormDesignUpdate::LinkActive :

            if (!inventDimFormSetupBOM)
            {
                inventDimFormSetupBOM  = InventDimCtrl::newFromFormValidDataSources(element, validBOM);
            }

            dimParmVisibleBOM.initProductDimensionsAllGroups();
            dimParmVisibleBOM.initSiteDimAllGrp();
            dimParmVisibleBOM.InventLocationIdFlag = NoYes::Yes;
            inventDimFormSetupBOM.parmDimParmEnabled(dimParmEnabledBOM);
            inventDimFormSetupBOM.parmDimParmVisibleGrid(dimParmVisibleBOM);

            if (!inventDimFormSetupPurchLine)
            {
                inventDimFormSetupPurchLine  = InventDimCtrl::newFromFormValidDataSources(element, validPurchLine);
            }

            dimParmVisiblePurchLine = EcoResProductDimGroupSetup::newItemId(callerPurchLine.ItemId).inventDimParmActiveDimensions();
            inventDimFormSetupPurchLine.parmDimParmEnabled(dimParmEnabledPurchLine);
            inventDimFormSetupPurchLine.parmDimParmVisible(dimParmVisiblePurchLine);
        //no break

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetupBOM.formSetControls(true);
            inventDimFormSetupPurchLine.formSetControls(true);

            break;
    }
}

public void init()
{
    super();

    bom.setTmp();
    bom.checkRecord(false);
}

void findBOMLines()
{
    container   conBOM;
    int         numBOMLines,i;

    tmpFrmVirtual = null;

    tmpBOM = null;
    tmpBOM.setTmp();

    if (callerDataSource.allowEdit() && ! purchLine.receivedInventInTotal() && ! purchLine.registered())
    {
        conBOM = BOMSearch::bomTreePackLevelConfig(callerPurchLine.ItemBOMId ? callerPurchLine.ItemBOMId : callerPurchLine.inventTable().bomId(callerPurchLine.deliveryDate(), callerPurchLine.QtyOrdered, callerPurchLine.inventDim()),
                                                   callerPurchLine.deliveryDate(),
                                                   callerPurchLine.ItemId,
                                                   callerPurchLine.inventDim().ConfigId,
                                                   true,
                                                   true);

        numBOMLines = conLen(conBOM);

        for (i=1; i<=numBOMLines; i++)
        {
            tmpBOM.data(conPeek(conBOM,i));
            tmpBOM.doInsert();
        }
    }

    bom.setTmpData(tmpBOM);
}

public int active()
{
    int ret;

    ret = super();
    element.updateDesign(InventDimFormDesignUpdate::LinkActive);

    return ret;
}

public void linkActive()
{
    element.setCaller();
    queryRecId.value(strFmt('%1',callerPurchLine.RecId));

    element.updateDesign(InventDimFormDesignUpdate::LinkActive);

    super();

    bom_ds.allowEdit(callerDataSource.allowEdit());
    bom_ds.findBOMLines();
    bom_ds.executeQuery();
}

void init()
{
    QueryBuildRange rangeIsDeleted;

    super();

    queryRecId = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine, RecId));

    rangeIsDeleted = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);
}
