[Form]
public class BOMExpandSales extends FormRun
{
    #resAppl

    FormRun               callerForm;
    SalesLine             callerSalesLine;
    FormDataSource        callerDataSource;

    TmpFrmVirtual         tmpFrmVirtual;
    BOM                   tmpBOM;
    QueryBuildRange       queryRecId;    

    InventDimCtrl_Frm     inventDimFormSetupBOM;
    InventDimCtrl_Frm     inventDimFormSetupSalesLine;
}

boolean  canClose()
{
    if (element.closedOk())
    {
        tmpFrmVirtual.initFromBomDS(BOM_DS);

        if (tmpFrmVirtual
            && callerDataSource.allowEdit()
            && (salesLine.SalesStatus == SalesStatus::None
                || salesLine.SalesStatus == SalesStatus::Backorder)
            && salesLine.salesTable().checkUpdate())
        {
            try
            {
                salesLine.expandBOM(tmpFrmVirtual, tmpBOM);
            }
            catch (Exception::Error)
            {
                return false;
            }
            FormDataUtil::getFormDataSource(salesLine).reread();
            FormDataUtil::getFormDataSource(salesLine).refresh();

            if (element.args().caller()
                && formHasMethod(element.args().caller(),identifierstr(interCompanyAutoCreateOrdersNow)))
            {
                element.args().caller().interCompanyAutoCreateOrdersNow();
            }
        }
    }

    return super();
}

void  init()
{
    if (! element.args().caller() || ! element.args().record())
    {
        throw error("@SYS22539");
    }

    super();

    if (#PmfEnabled)
    {
        if (callerSalesLine.inventTable().isFormulaAllowed())
        {
            element.design().caption("@PRO484");
        }
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void setCaller()
{
    callerForm          = element.args().caller();
    callerSalesLine     = element.args().record();
    callerDataSource    = FormDataUtil::getFormDataSource(element.args().record());
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm dimParmVisibleBOM, dimParmEnabledBOM, dimParmVisibleSalesLine, dimParmEnabledSalesLine;
    Set           validBOM, validSalesLine;

    validBOM        = new Set(Types::String);
    validSalesLine  = new Set(Types::String);

    validBOM.add(inventDimBOM_ds.name());
    validSalesLine.add(inventDimSalesLine_ds.name());

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init,
             InventDimFormDesignUpdate::LinkActive :

            element.setCaller();

            if (!inventDimFormSetupBOM)
            {
                inventDimFormSetupBOM  = InventDimCtrl::newFromFormValidDataSources(element, validBOM);
            }

            dimParmVisibleBOM.initProductDimensionsAllGroups();
            dimParmVisibleBOM.initSiteDimAllGrp();
            dimParmVisibleBOM.InventLocationIdFlag = NoYes::Yes;
            inventDimFormSetupBOM.parmDimParmEnabled(dimParmEnabledBOM);
            inventDimFormSetupBOM.parmDimParmVisibleGrid(dimParmVisibleBOM);

            if (!inventDimFormSetupSalesLine)
            {
                inventDimFormSetupSalesLine  = InventDimCtrl::newFromFormValidDataSources(element, validSalesLine);
            }

            dimParmVisibleSalesLine = EcoResProductDimGroupSetup::newItemId(callerSalesLine.ItemId).inventDimParmActiveDimensions();
            inventDimFormSetupSalesLine.parmDimParmEnabled(dimParmEnabledSalesLine);
            inventDimFormSetupSalesLine.parmDimParmVisible(dimParmVisibleSalesLine);
        //no break

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetupBOM.formSetControls(true);
            inventDimFormSetupSalesLine.formSetControls(true);

            break;
    }
}

void findBOMLines()
{
    container   conBOM;
    int         numBOMLines, i;

    tmpFrmVirtual = null;

    tmpBOM = null;
    tmpBOM.setTmp();

    if (callerDataSource.allowEdit() && ! salesLine.deliveredInventInTotal() && ! salesLine.pickedInTotalInventUnit() )    // no updates must have been executed
    {
        conBOM = BOMSearch::bomTreePackLevelConfig(callerSalesLine.ItemBOMId ? callerSalesLine.ItemBOMId : callerSalesLine.inventTable().bomId(callerSalesLine.deliveryDate(), callerSalesLine.QtyOrdered, callerSalesLine.inventDim()),
                                                   callerSalesLine.deliveryDate(),
                                                   callerSalesLine.ItemId,
                                                   callerSalesLine.inventDim().ConfigId,
                                                   true,
                                                   true);

        numBOMLines = conlen(conBOM);

        for (i=1; i<=numBOMLines; i++)
        {
            tmpBOM.data(conpeek(conBOM,i));
            this.setTmpBOMInventLocationId(tmpBOM);
            tmpBOM.doInsert();
        }
    }

    bom.setTmpData(tmpBOM);
}

public void init()
{
    super();

    bom.setTmp();
    bom.checkRecord(false);
}

private void setTmpBOMInventLocationId(BOM _tempBOM)
{
    if(!_tempBOM.InventDim().InventLocationId
        && EcoResStorageDimensionGroup::find(EcoResStorageDimensionGroupItem::findByItem(_tempBOM.DataAreaId, _tempBOM.ItemId).StorageDimensionGroup).IsWarehouseMandatoryEnabled)
    {
        InventDim salesLineDim = callerSalesLine.inventDim();
        InventDim inventDimNew;
        inventDimNew = _tempBOM.BOMMap::inventDimFinal(callerSalesLine.ItemId, salesLineDim, _tempBOM.ItemRouteId);
        if (!inventDimNew.InventLocationId && inventDimNew.InventSiteId == salesLineDim.InventSiteId && salesLineDim.InventLocationId)
        {
            inventDimNew.InventLocationId = salesLineDim.InventLocationId;
        }
        _tempBOM.modifiedInventDim(inventDimNew, fieldNum(InventDim, InventLocationId));
    }
}

public int active()
{
    int ret;

    ret = super();
    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void linkActive()
{
    element.setCaller();
    queryRecId.value(strfmt('%1',callerSalesLine.RecId));

    element.updateDesign(InventDimFormDesignUpdate::LinkActive);

    super();

    bom_ds.allowEdit(callerDataSource.allowEdit());
    bom_ds.findBOMLines();
    bom_ds.executeQuery();
}
