[Form]
public class BOMReportFinishMax extends FormRun
{
    #resAppl

    BOMReportFinish_Max     reportFinishMax;

    int                     curParentId;

    ImageListAppl_Prod      imageList;

    InventDimCtrl_Frm_Setup inventDimFormSetupParm;
    InventDimCtrl_Frm       inventDimFormSetupTmp;

    InventDimParm           inventDimParmTmpShow;

    PdsCWFormCtrl           cwFormCtrl;

    private boolean         bomReportFinishMaxLinkActiveEnabled = BOMReportFinishMaxLinkActiveFlight::instance().isEnabled();

}

void buildTree()
{
    TmpBOM          tmpBOMtree;
    int             levelParentId[];
    FormTreeItem    treeItem;
    int             rootId,rawMatRootId,coRootId;

    ctrlTree.deleteAll();
    curParentId = 0;

    tmpBOMtree.setTmpData(tmpBOM);

    select firstonly forupdate tmpBOMtree
        where  tmpBOMTree.Level     == 0
            && tmpBOMtree.ItemId    == BOMParmReportFinish.ItemId;

    tmpBOMtree.TreeId = ctrlTree.add(
        0,
        0,
        this.nodeTxt(tmpBOMtree),
        this.nodeImage(tmpBOMtree.ItemId));
    treeItem = ctrlTree.getItem(tmpBOMtree.TreeId);
    treeItem.stateBold(true);
    treeItem.children(true);
    rootId = tmpBOMtree.TreeId;
    treeItem.data(tmpBOMtree.RecId);
    ctrlTree.setItem(treeItem);

    tmpBOMtree.update();

    rawMatRootId = ctrlTree.add(rootId, 0, "@PDS1444", 0);

    treeItem = ctrlTree.getItem(rawMatRootId);
    treeItem.stateBold(true);
    treeItem.children(true);
    treeItem.data(tmpBOMtree.RecId);
    ctrlTree.setItem(treeItem);

    levelParentId[1] = rawMatRootId;

    coRootId = ctrlTree.add(rootId, 0, "@PDS1445", 0);

    treeItem = ctrlTree.getItem(coRootId);
    treeItem.stateBold(true);
    treeItem.children(true);
    treeItem.data(tmpBOMtree.RecId);
    ctrlTree.setItem(treeItem);

    while select forupdate tmpBOMtree
        order by Level, LineNum
        where   tmpBOMtree.Level     == 0
                && tmpBOMtree.ItemId    != BOMParmReportFinish.ItemId
    {
        tmpBOMtree.TreeId = ctrlTree.add(
            coRootId,
            0,
            this.nodeTxt(tmpBOMtree),
            this.nodeImage(tmpBOMtree.ItemId));
        treeItem = ctrlTree.getItem(tmpBOMtree.TreeId);
        treeItem.data(tmpBOMtree.RecId);
        ctrlTree.setItem(treeItem);

        tmpBOMtree.update();
    }

    while select forupdate tmpBOMtree
        order by LineNum
        where tmpBOMtree.Level > 0
    {
        if (tmpBOMtree.Level)
        {
            curParentId = levelParentId[tmpBOMtree.Level];
        }

        tmpBOMtree.TreeId = ctrlTree.add(
            curParentId,
            0,
            this.nodeTxt(tmpBOMtree),
            this.nodeImage(tmpBOMtree.ItemId));
        treeItem = ctrlTree.getItem(tmpBOMtree.TreeId);
        if (! tmpBOMtree.bomLine)
        {
            levelParentId[tmpBOMtree.Level+1] = tmpBOMtree.TreeId;
            treeItem.stateBold(true);
            treeItem.children(true);
        }

        treeItem.data(tmpBOMtree.RecId);
        ctrlTree.setItem(treeItem);

        tmpBOMtree.update();
    }
}

void doSelectionChange(FormTreeItem _newItem)
{
    TmpBOM  tmpBOMfind;

    tmpBOMfind.setTmpData(tmpBOM);

    select tmpBOMfind
        where tmpBOMfind.RecId == _newItem.data();

    if (tmpBOMfind)
    {
        tmpBOM_ds.findRecord(tmpBOMfind);
    }
}

void init()
{
    if (bomReportFinishMaxLinkActiveEnabled)
    {
        reportFinishMax = element.initNewBOMReportFinishMax();
    }
    else
    {
        InventTable inventTable = element.retrieveInventTable();

        reportFinishMax = element.initializeBOMReportFinishMax();
        reportFinishMax.createBOMParmReportFinish(inventTable);
    }

    cwFormCtrl = PdsCWFormCtrl::constructFromForm(element);
    cwFormCtrl.parmFieldIds(PdsCWValidation::fieldMapFromContainers(
        [[fieldnum(BOMParmReportFinish, Qty), fieldnum(BOMParmReportFinish, PdsCWQty), false]] ));
    cwFormCtrl.parmItemFieldId(fieldnum(BOMParmReportFinish, ItemId));

    super();

    BOMParmReportFInish_ds.query().dataSourceTable(tablenum(BOMParmReportFinish)).clearDynalinks();
    cwFormCtrl.setDatasources(BOMParmReportFinish);

    imageList = new ImageListAppl_Prod();
    ctrlTree.setImagelist(imageList.imageList());

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

