boolean inventDimParmComplete()
{
    InventDimParm       inventDimParmMandatory;
    InventDimParm       inventDimParmSpecified;

    inventDimParmMandatory.setActiveSiteAndProductDimensions(InventDimGroupSetup::newItemId(bomParmReportFinish.ItemId));
    inventDimParmSpecified.initFromInventDim(inventDimParm);

    // ignore non mandatory dimensions
    inventDimParmSpecified = InventDimParm::andParms(inventDimParmSpecified,inventDimParmMandatory);

    return InventDimParm::isInventDimParmEqual(inventDimParmSpecified,inventDimParmMandatory);
}

Object inventDimSetupObject()
{
    if (this.selectedControl().name() == inventDimParmFixedParm.name())
    {
        return inventDimFormSetupParm;
    }
    return inventDimFormSetupTmp;
}

int nodeImage(ItemId _itemId)
{
    return imageList.image(InventTable::find(_itemId).inventItemType().imageRessNo());
}

str nodeTxt(TmpBOM _bom)
{
    str                 lineTxt;
    InventTable         inventTable = InventTable::find(_bom.ItemId);
    Integer             qtyDecimals;
    EcoResProductName   productName;

    if (inventTable.inventUnitId())
    {
        qtyDecimals = UnitOfMeasure::unitOfMeasureDecimalPrecision(UnitOfMeasure::unitOfMeasureIdBySymbol(inventTable.inventUnitId()));
    }
    else
    {
        qtyDecimals = 2;
    }

    productName = inventTable.defaultProductName();

    lineTxt += (lineTxt ? ' / ' : '') + (_bom.ItemId                ? _bom.ItemId                           : '--');
    lineTxt += (lineTxt ? ' / ' : '') + (_bom.inventDim().ConfigId  ? _bom.inventDim().ConfigId             : '--');
    lineTxt += (lineTxt ? ' / ' : '') + (productName                ? productName                           : '--');
    lineTxt += (lineTxt ? ' / ' : '') + (_bom.PdsCWQty              ? num2str(_bom.PdsCWQty,0,0,0,0)        : '--');
    lineTxt += (lineTxt ? ' / ' : '') + (_bom.Qty                   ? num2str(_bom.Qty,0,qtyDecimals,0,0)   : '--');
    lineTxt += (lineTxt ? ' / ' : '') + (inventTable.inventUnitId() ? inventTable.inventUnitId()            : '--');

    return lineTxt;
}

void update()
{
    TmpBOM                  tmpBOMempty;

    if (   ! boMparmReportFinish.ItemId
        || ! boMparmReportFinish.Qty
        || ! boMparmReportFinish.bomId
        || ! boMparmReportFinish.InventDimId
        || ! InventTable::find(boMparmReportFinish.ItemId).inventItemType().canHaveBOM())
    {
        tmpBOM.setTmpData(tmpBOMempty);
        tmpBOM_ds.research();
    }
    else
    {
        reportFinishMax = this.initializeBOMReportFinishMaxWithParmBuffer();
        reportFinishMax.parmChildInventDimParm(inventDimParmTmpShow);
        reportFinishMax.run();

        tmpBOM.setTmpData(reportFinishMax.tmpBOM());

        element.buildTree();
        tmpBOM_ds.research();
    }
}

