void updateBOMId()
{
    if (    bomParmReportFinish.ItemId
        &&  bomParmReportFinish.TransDate
        &&  bomParmReportFinish.Qty
        &&  element.inventDimParmComplete())
    {
        bomParmReportFinish.bomId = this.retrieveParmReportFinishBomId();
    }
    else
    {
        bomParmReportFinish.bomId = '';
    }

    element.update();
}

/// <summary>
/// Retrieves bom id for report as finished parameter.
/// </summary>
/// <returns>
/// Bom id.
/// </returns>
protected BOMId retrieveParmReportFinishBomId()
{
    return InventTable::find(bomParmReportFinish.ItemId).bomId(bomParmReportFinish.TransDate, bomParmReportFinish.Qty, inventDimParm);
}

void updateDesign(
    InventDimFormDesignUpdate   _mode,
    identifierName              _dsName = ''
    )
{
    Set             set;
    InventDimParm   inventDimParmTmpEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (! inventDimFormSetupParm)
            {
                inventDimFormSetupParm  = InventDimCtrl_Frm_Setup::newFromFormGridField(element,fieldnum(InventDimSetupGrid,ShowGridBOMMain));
            }
            if (! inventDimFormSetupTmp)
            {
                set = new Set(Types::String);
                set.add(inventDimTmp_ds.name());
                inventDimFormSetupTmp  = InventDimCtrl::newFromFormValidDataSources(element,set);
                inventDimParmTmpShow.initDimBOMAll();
                inventDimFormSetupTmp.parmDimParmVisibleGrid(inventDimParmTmpShow);
            }

        case InventDimFormDesignUpdate::Active :
            switch (_dsName)
            {
                case  bomParmReportFinish_ds.name():
                    inventDimFormSetupParm.formActiveSetup(InventDimGroupSetup::newItemId(bomParmReportFinish.ItemId));
                    inventDimFormSetupParm.parmDimParmVisibleGrid(inventDimFormSetupParm.parmDimParmEnabled());
                    inventDimFormSetupParm.formSetControls(true);
                    break;
                case tmpBOM_ds.name():
                    inventDimParmTmpEnabled.initDimBOM(InventDimGroupSetup::newItemId(tmpBOM.ItemId));
                    inventDimFormSetupTmp.parmDimParmEnabled(inventDimParmTmpEnabled);
                    inventDimFormSetupTmp.formSetControls(true);
                    break;
                default:
                    inventDimFormSetupParm.formSetControls(true);
                    inventDimFormSetupTmp.formSetControls(true);
            }

            break;

        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetupParm.formActiveSetup(InventDimGroupSetup::newItemId(tmpBOM.ItemId));
            inventDimFormSetupParm.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

