public void init()
{
    InventTable inventTable = element.retrieveInventTable();

    super();
    if (inventTable.PmfProductType == PmfProductType::By_Product
        || inventTable.PmfProductType == PmfProductType::Co_Product)
    {
        this.query().dataSourceTable(tablenum(BOMParmReportFinish)).addDynalink(
            fieldNum(BOMParmReportFinish, ItemId),
            inventTable,
            fieldnum(InventTable, PmfPlanningItemId));
    }

    bomParmReportFinish.setTmp();
    bomParmReportFinish.checkRecord(false);
    bomParmReportFinish.setTmpData(reportFinishMax.parmParmBuffer());

    cwFormCtrl.dataSourceInitPost(BOMParmReportFinish);
}

public boolean validateWrite()
{
    bomParmReportFinish.InventDimId = InventDim::findOrCreate(inventDimParm).InventDimId;

    return true;
}

public void linkActive()
{  
    if (bomReportFinishMaxLinkActiveEnabled)
    {
        reportFinishMax = element.initNewBOMReportFinishMax();

        bomParmReportFinish.setTmpData(reportFinishMax.parmParmBuffer());

        cwFormCtrl.dataSourceInitPost(BOMParmReportFinish);
    }

    super();
}

