/// <summary>
/// Edit or create the configuration nomenclature.
/// </summary>
public void editConfigurationNomenclature()
{
    EcoResDimBasedConfigurationNomenclature nomenclature;

    if (!EcoResBOMDimBasedConfigurationNomenclature || !EcoResBOMDimBasedConfigurationNomenclature.DimBasedConfigurationNomenclature)
    {
        if (Box::yesNo("@SCM:NoNomenclatureDefined", DialogButton::Yes) == DialogButton::Yes)
        {
            ttsbegin;

            nomenclature.Name = BOMTable.BOMId;
            nomenclature.insert();

            EcoResBOMDimBasedConfigurationNomenclature.NomenclatureRole = EcoResNomenclatureRole::Id;
            EcoResBOMDimBasedConfigurationNomenclature.BOMId = BOMTable.BOMId;
            EcoResBOMDimBasedConfigurationNomenclature.DimBasedConfigurationNomenclature = nomenclature.RecId;
            EcoResBOMDimBasedConfigurationNomenclature.write();

            ttscommit;
        }
    }
    else
    {
        nomenclature = EcoResDimBasedConfigurationNomenclature::find(EcoResBOMDimBasedConfigurationNomenclature.DimBasedConfigurationNomenclature);
    }

    if (nomenclature)
    {
        nomenclature.editInSimpleForm(BOMTable, this);
    }
}

/// <summary>
/// Gets the buffer used as context when opening the <c>DocuView</c> form.
/// </summary>
/// <returns>The document context buffer.</returns>
/// <remarks>
/// This method is overridden to ensure that the document context is set to the bill of materials
/// or bill of materials version even if the selected control is bound to an inventory dimension.
/// </remarks>
public Common docCursor()
{
    Common docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        InventDim inventDim = docCursor;
        if (bom.InventDimId == inventDim.InventDimId)
        {
            docCursor = bom;
        }
        else if (bomVersion.InventDimId == inventDim.InventDimId)
        {
            docCursor = bomVersion;
        }
    }

    return docCursor;
}

void checkBOM(BOMId bomId)
{
    if (!needCheckBOM || ! bomId)
    {
        return;
    }

    if (! BOMTable::find(bomId).CheckBOM)
    {
        return;
    }

    BOMHierarchyCheck::checkBOM(bomId,
                                ! BOMParameters::find().mustCheckCircularityManual());

    needCheckBOM = false;
}

