/// <summary>
/// Checks whether the route operation lookups created by this form should be skipped.
/// </summary>
/// <returns>true if the lookups should be skipped; otherwise, false.</returns>
public boolean skipRouteOperationNumberLookup()
{
    return !this.isSingleBOMVersionForCurrentBOM();
}

private boolean isSingleBOMVersionForCurrentBOM()
{
    BOMVersion bomVersionCount;

    select count(RecId) from bomVersionCount
        where bomVersionCount.BOMId == this.bomId();

    return (bomVersionCount.RecId == 1);
}

