public boolean canClose()
{
    boolean ret = super();
    boolean bomCheckedOk = true;

    if (ret
        && (element.closedOk() || (!element.closedOk() && !element.closedCancel()))
        && bom.bomId
        && bom.ItemId
        && (!bom.RecId || bom.RecId && bom.ItemId != bom.orig().ItemId))
    {
        bom_ds.write();
    }

    if (needCheckBOM && bom.bomId && BOMParameters::find().mustCheckBOMCircularityBOM())
    {
        bomCheckedOk = BOMHierarchyCheck::checkBOMCircularity(bom.bomId);
        BOMTable::setCheckBOM(bom.bomId, bomCheckedOk);
    }

    // a box needs to be opened in order to prevent the form from closing without informing the user of the BOM circulation
    if (!bomCheckedOk && Box::yesNo("@SYS122835", DialogButton::No) == DialogButton::Yes)
    {
        bomCheckedOk = true;
    }

    needCheckBOM = false;

    return ret && bomCheckedOk;
}

void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    if (bomVersion.bomId && bomVersion.bomId != naStr())
    {
        element.checkBOM(bomVersion.bomId);
    }

    if (element.args() && element.args().caller())
    {
        FormRun callerForm = element.args().caller() as FormRun;
        if (callerForm != null && callerForm.name() == formstr(BOMConsistOf) && formHasMethod(callerForm, identifierStr(doRefresh)))
        {
            callerForm.doRefresh();
        }
    }

    super();
}

void doRefresh(boolean _executeBOMVersionQuery = false)
{
    bomTable_ds.reread();
    bomTable_ds.refresh();
    bomTable_ds.rereadReferenceDataSources();
    if (_executeBOMVersionQuery)
    {
        bomVersion_ds.executeQuery();
    }
    else
    {
        bomVersion_ds.reread();
        bomVersion_ds.refresh();
        bomVersion_ds.rereadReferenceDataSources();
    }
}

void doResearch(boolean _doBOM = true)
{
    bomTable_ds.research(true);
    if (_doBOM)
    {
        bom_ds.research();
    }
    else
    {
        bomVersion_ds.research();
    }
}

void enableBOMTable()
{
    boolean noEdit =  BOMParameters::find().BlockEditing;

    bomTable_ds.object(fieldNum(BOMTable,Name)).allowEdit(!bomTable.Approved || !noEdit);
    bomTable_ds.object(fieldNum(BOMTable,ItemGroupId)).allowEdit(!bomTable.Approved || !noEdit);
    if (#pmfEnabled)
    {
        bomTable_ds.object(fieldNum(BOMTable,SiteId)).allowEdit(!bomTable.Approved || !noEdit || !BOMVersion.isApproved());
    }
}

