    boolean allowEditBOMVersion = bomVersion.allowEdit(bomParameters);

    bomVersion_ds.object(fieldNum(BOMVersion,ItemId)).allowEdit(allowEditBOMVersion);
    bomVersion_ds.object(fieldNum(BOMVersion,FromDate)).allowEdit(allowEditBOMVersion);
    bomVersion_ds.object(fieldNum(BOMVersion,ToDate)).allowEdit(allowEditBOMVersion);
