    if (!#PMFEnabled && enum2int(bomTable.PmfBomFormula) != enum2int(PmfBOMFormula::Formula))
    {
        bomVersion_ds.object(fieldNum(BOMVersion,FromQty)).allowEdit(allowEditBOMVersion);
    }
}

