void enableBOMFields()
{
    InventItemType  inventItemType = bom.inventTable().inventItemType();
    InventTable     inventTable = bom.inventTable();

    bom_ds.object(fieldNum(BOM,ItemBOMId)).allowEdit(inventTable.isFormulaOrBOMAllowed());
    bom_ds.object(fieldNum(BOM,ItemRouteId)).allowEdit(inventItemType.canHaveRoutes());
    bom_ds.object(fieldNum(BOM,ProjSetSubProdToConsumed)).allowEdit(bom.bomType == BOMType::PeggedSupply);

    element.enableWarehouseProcessFields();
    element.pdsEnableBOMFields();
}

private void enableWarehouseProcessFields()
{
    WarehouseProcesses_OverrideMaterialOverpick.visible(isWarehouseProductionRawMaterialOverpickingFlightEnabled);

    if (isWarehouseProductionRawMaterialOverpickingFlightEnabled)
    {
        boolean isSettingDefault = this.isOverrideMaterialOverpickSettingDefault();

        WarehouseProcesses_OverrideMaterialOverpick.value(!isSettingDefault);
        bom_ds.object(fieldnum(BOM, MaterialOverpickPercentage)).allowEdit(!isSettingDefault);
    }
}

private boolean isOverrideMaterialOverpickSettingDefault()
{
    return (WarehouseProcesses_MaterialOverpickPercentage.realValue() == BOM.whsInventTable().MaterialOverpickPercentage);
}

public void pdsEnableBOMFields()
{
    PmfFormulaCoBy                          coByFormula;
    FieldId                                 batchFieldId = fieldNum(InventDim, InventBatchId);
    InventTable                             endItem;
    InventTable                             rawItem;
    InventTable                             coProduct;
    EcoResTrackingDimensionGroupFldSetup    rawTrkDimSetup;
    EcoResTrackingDimensionGroupFldSetup    endTrkDimSetup;
    boolean                                 endItemEnabled;
    boolean                                 coProductEnabled;
    BOMVersion                              version;

    bom_ds.object(fieldNum(BOM, bomConsump)).enabled(bom.pdsIngredientType == PDSIngredientTypeEnum::None);

    if (#PDSLotGenealogyEnabled)
    {
        //Check that Component is batch tracked and is corect type
        rawItem     = bom.inventTable();

        rawTrkDimSetup = EcoResTrackingDimensionGroupFldSetup::findByDimensionGroupFieldId(rawItem.trackingDimensionGroup(),batchFieldId);

        if (!rawTrkDimSetup.IsActive
            || bom.bomType == BOMType::Phantom
            || bom.bomType == BOMType::Vendor)
        {
            bom_ds.object(fieldNum(BOM, pdsInheritCoProductShelfLife)).allowEdit(false);
            bom_ds.object(fieldNum(BOM, pdsInheritEndItemShelfLife)).allowEdit(false);
            bom_ds.object(fieldNum(BOM, pdsInheritEndItemBatchAttrib)).allowEdit(false);
            bom_ds.object(fieldNum(BOM, pdsInheritCoProductBatchAttrib)).allowEdit(false);
            pdsBatchAttribTransfer.enabled(false);
            return;
        }

        // select a valid end item
        select firstonly ItemId, Product, PmfProductType, Phantom
            from endItem
            exists join version
            where version.bomId   == bom.bomId
                && version.ItemId == endItem.ItemId;

        // for phantom we disable all inheritance
        if (endItem.Phantom)
        {
            bom_ds.object(fieldNum(BOM, pdsInheritCoProductShelfLife)).allowEdit(false);
            bom_ds.object(fieldNum(BOM, pdsInheritEndItemShelfLife)).allowEdit(false);
            bom_ds.object(fieldNum(BOM, pdsInheritEndItemBatchAttrib)).allowEdit(false);
            bom_ds.object(fieldNum(BOM, pdsInheritCoProductBatchAttrib)).allowEdit(false);
            pdsBatchAttribTransfer.enabled(false);
            return;
        }

        endTrkDimSetup   = EcoResTrackingDimensionGroupFldSetup::findByDimensionGroupFieldId(endItem.trackingDimensionGroup(),batchFieldId);
        endItemEnabled   = endTrkDimSetup.IsActive && endItem.PmfProductType != PmfProductType::MainItem;
        bom_ds.object(fieldNum(BOM, pdsInheritEndItemShelfLife)).allowEdit(endItemEnabled);
        bom_ds.object(fieldNum(BOM, pdsInheritEndItemBatchAttrib)).allowEdit(endItemEnabled);

        // select a valid co product
        while select Product,ItemId
            from coProduct
            exists join coByFormula
                where coByFormula.ItemId == coProduct.ItemId
                    && coByFormula.bomId == bom.bomId
        {
            if (EcoResTrackingDimensionGroupFldSetup::findByDimensionGroupFieldId(coProduct.trackingDimensionGroup(), batchFieldId).IsActive)
            {
                coProductEnabled = true;
                break;
            }
        }

        bom_ds.object(fieldNum(BOM, pdsInheritCoProductShelfLife)).allowEdit(coProductEnabled);
        bom_ds.object(fieldNum(BOM, pdsInheritCoProductBatchAttrib)).allowEdit(coProductEnabled);

        pdsBatchAttribTransfer.enabled(bom.pdsInheritEndItemBatchAttrib || bom.pdsInheritCoProductBatchAttrib);
    }
}

public void enableStepConsumption(BOM _bom)
{
    if (#PmfEnabled)
    {
        if (bom.Formula == BOMFormula::Formula5)
        {
            cwFormCtrl_BOMTable_BOMStep.setDatasources(pmfBOMStep, bom);
            cwFormCtrl_BOMTable_BOMStep.dataSourceLinkActivePost();
        }

        boolean enable = _bom.Formula == BOMFormula::Formula5
                    && (!BOMParameters::find().BlockEditing || !_bom.bomTable().isApproved());

        pmfBOMStep_ds.allowCreate(enable);
        pmfBOMStep_ds.allowEdit(enable);
        pmfBOMStep_ds.allowDelete(enable);
        pmfBOMStep_ds.insertIfEmpty(enable);
    }
}

