void init()
{
    InventTable inventTable;
    InventDim   inventDim;

    pmfFormCtrl_BOMTable_BOMTable = PmfFormCtrl_BOMTable_BOMTable::newFromForm(element);
    pmfFormCtrl_BOMTable_BOMVersion = PmfFormCtrl_BOMTable_BOMVersion::newFromForm(element);

    pmfFormCtrl_BOMTable_BOMTable.setDatasources(bomTable);
    pmfFormCtrl_BOMTable_BOMVersion.setDatasources(bomVersion, inventDimBOMVersion);

    pmfFormCtrl_BOMTable_BOM = PmfFormCtrl_BOMTable_BOM::newFromForm(element);
    cwFormCtrl_BOMTable_BOMStep = PdsCWFormCtrl_BOMTable_BOMStep::newFromForm(element);

    pmfIsFormulaEnabled = pmfFormCtrl_BOMTable_BOMTable.isFormulaEnabled();

    Args args = element.args();
    if (args)
    {
        if (args.dataset() == tableNum(InventTable))
        {
            inventTable = args.record();
        }
        else if (args.dataset() == tableNum(InventSum))
        {
            InventSum inventSum = args.record();
            inventTable = inventSum.inventTable();
        }
        if (inventTable)
        {
            if (pmfIsFormulaEnabled && !inventTable.isFormulaAllowed())
            {
                throw error(strfmt("@PRO154", inventTable.ItemId));
            }

            if (!inventTable.isBOMAllowed() && !pmfIsFormulaEnabled)
            {
                throw error(strFmt("@SYS19326",inventTable.ItemId));
            }

            inventDim.InventSiteId  = inventTable.inventInventSiteId();
            inventDim               = InventDim::findOrCreate(inventDim);

            if (pmfIsFormulaEnabled)
            {
                if (!inventTable.bomId(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),1,inventDim))
                {
                    throw error(strfmt("@PRO153", inventTable.ItemId));
                }
            }
            else
            {
                if (!inventTable.bomId(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                    inventTable.inventStandardQty(inventDim.InventDimId),
                                    inventDim))
                {
                    throw error(strfmt("@SYS25028", inventTable.ItemId));
                }
            }
        }
    }

    Set formulaEnumSet = PmfFormCtrl_BOMHelper::buildFormulaEnumSet(pmfIsFormulaEnabled);
    formulaComboBox = SysFormEnumComboBox::newParameters(element, element.controlId(formControlStr(BOMTable, Formula)),  enumNum(BOMFormula), formulaEnumSet);

    super();

    pmfFormCtrl_BOMTable_BOM.setDatasources(bom, inventDimBOM);
    cwFormCtrl_BOMTable_BOMStep.setDatasources(pmfBOMStep, bom);

    boolean versionVisible = new DictTable(tableNum(BOMVersion)).rights() >= AccessType::View &&
                             isConfigurationkeyEnabled(configurationKeyNum(BOMVersion));

    HeaderDetailsVersion.visible(versionVisible);
    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.updateDesignBOM(InventDimFormDesignUpdate::Init);

    bomParameters = BOMParameters::find();

    if (bomParameters.MandatoryQtyAndDate)
    {
        bomVersion_FromDate.mandatory(true);
        bomVersion_ToDate.mandatory(true);
    }
    pmfFormCtrl_BOMTable_BOMTable.initPost();
    pmfFormCtrl_BOMTable_BOMVersion.initPost();
    pmfFormCtrl_BOMTable_BOM.initPost();

    this.enableEngineeringChangeButtons();

    if (!element.args() || element.args().openMode() != OpenMode::New)
    {
        navigationList.visible(!element.args() || !element.args().record());
    }

    BomWHSReleasePolicy.visible(BOMReleaseToWarehouseVisibleFlight::instance().isEnabled());
}

