            numberSeqFormHandler = NumberSeqFormHandler::newForm(BOMParameters::numRefBOMId().NumberSequenceId,
                                                                 element,
                                                                 bomTable_DS,
                                                                 fieldNum(BOMTable,bomId)
                                                                );
        }
    }
    return numberSeqFormHandler;
}

