void updateDesign(
    InventDimFormDesignUpdate   _mode,
    InventTable                 _inventTable = null)
{
    if (!HeaderDetailsVersion.visible())
    {
        return;
    }

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetupBOMVersion)
            {
                Set validDataSources = new Set(Types::String);
                validDataSources.add(bomVersion_ds.name());
                validDataSources.add(inventDimBOMVersion_ds.name());
                inventDimFormSetupBOMVersion = InventDimCtrl::newFromFormValidDataSources(element, validDataSources);
            }

            InventDimParm inventDimParmShow;
            inventDimParmShow.initDimBOMRouteVersion();
            inventDimFormSetupBOMVersion.parmDimParmVisibleGrid(inventDimParmShow);
            inventDimFormSetupBOMVersion.formSetControls(true);

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::LinkActive,
             InventDimFormDesignUpdate::FieldChange :

            if (_inventTable)
            {
                InventDimParm inventDimParmEnable;
                inventDimParmEnable.initDimBOMVersionEnable(InventDimGroupSetup::newInventTable(_inventTable), bomVersion);
                inventDimFormSetupBOMVersion.parmDimParmEnabled(inventDimParmEnable);
                inventDimFormSetupBOMVersion.formSetControls(true);
            }
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

void updateDesignBOM(
    InventDimFormDesignUpdate   _mode,
    InventTable                 _inventTable = bom.inventTable())
{
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;
    Set             validDataSources;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetupBOM)
            {
                validDataSources = new Set(Types::String);
                validDataSources.add(bom_ds.name());
                validDataSources.add(inventDimBOM_DS.name());
                inventDimFormSetupBOM = InventDimCtrl::newFromFormValidDataSources(element,validDataSources);
            }
            inventDimFormSetupBOM.parmSkipOnHandLookUp(true);
            inventDimParmShow.initDimBOMAll();
            inventDimParmShow.InventSiteIdFlag = false;
            inventDimFormSetupBOM.parmDimParmVisibleGrid(inventDimParmShow);

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange :
            inventDimParmEnabled.initDimBOMEnable(InventDimGroupSetup::newInventTable(_inventTable),bom);
            inventDimFormSetupBOM.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetupBOM.formSetControls(true);
            BOMConsistOfAction.visible(true);
            if (FeatureStateProvider::isFeatureEnabled(DisableBomVersionButtonForInactiveBomItemsFlighting::instance()))
            {
                InventDim inventDim;
                BOMVersion bomVersionExists = BOMVersion::selectBomVersion(_inventTable.ItemId, dateNull(), maxUInt(), inventDim, true, true, true, true, false);
                BOMConsistOfAction.enabled((_inventTable.isBOMAllowed() || _inventTable.isFormulaAllowed()) && bomVersionExists);
            }
            else
            {
                BOMConsistOfAction.enabled(_inventTable.isBOMAllowed() || _inventTable.isFormulaAllowed());
            }
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public boolean validateWrite()
{
    boolean ret;

    BOMTable tmp = this.cursor();

    EcoResBOMDimBasedConfigurationNomenclature.BOMId = tmp.BOMId;

    ret = super();

    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    return ret;
}

void write()
{
    ttsbegin;

    super();

    element.numberSeqFormHandler().formMethodDataSourceWrite();

    ttscommit;
}

void linkActive()
{
    InventTable inventTable;
    InventDim   inventDim;

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    if (element.args().dataset() == tablenum(InventTable))
    {
        inventTable= element.args().record();
    }
    else if (element.args().dataset() == tableNum(InventSum))
    {
        InventSum inventSum = element.args().record();
        inventTable = inventSum.inventTable();
    }

    if (inventTable)
    {
        if (inventTable.isBOMAllowed() || pmfIsFormulaEnabled)
        {
            inventDim.InventSiteId  = inventTable.inventInventSiteId();
            inventDim               = InventDim::findOrCreate(inventDim);

            if (pmfIsFormulaEnabled)
            {
                bomNum = inventTable.bomId(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                            1,
                                            inventDim);
            }
            else
            {
                bomNum = inventTable.bomId(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                            inventTable.inventStandardQty(inventDim.InventDimId),
                                            inventDim);
            }
            if (!bomNum)
            {
                bomNum  = naStr();
            }
        }
        else
        {
            bomNum  = naStr();
        }

        criteriaBOMId.value(queryValue(bomNum));
    }

    super();

    if (bomNum == naStr())
    {
        bomConfigRoute_ds.research();
    }
}

void init()
{
