int active()
{
    int ret;

    ret = super();

    pmfFormCtrl_BOMTable_BOMVersion.dataSourceActivePost();
    pmfFormCtrl_BOMTable_BOMVersion.parmInventTable(bomVersion.inventTable());

    if (#pmfEnabled)
    {
        element.setBOMLines();
        element.setCoByProducts();
    }
    element.setBOMVersionApprove();
    element.setControlDecimals();
    element.enableBOMVersion();
    element.updateDesign(InventDimFormDesignUpdate::Active,bomVersion.inventTable());

    return ret;
}

public void write()
{
    InventDim   inventDim;
    Boolean      calcFlagChanged    = false;
    Boolean      calcFlagUpdateAll  = false;
    Boolean      formulaSizeChanged = false;
    BOMVersion   bomVersionCheck;
    RecVersion   oldRecVersion = bomVersion.RecVersion;

    if (bomVersion.orig())
    {
        calcFlagChanged = bomVersion.PmfFormulaVersionCalculation != bomVersion.orig().PmfFormulaVersionCalculation;
        formulaSizeChanged = bomVersion.PmfBatchSize != bomVersion.orig().PmfBatchSize;
    }
    else
    {
        calcFlagChanged = true;
        formulaSizeChanged = true;
    }
    pmfFormCtrl_BOMTable_BOMVersion.dataSourceWritePre();
    ttsbegin;

    if (   #pmfEnabled
        && BOMTable.PmfBomFormula == PmfBOMFormula::Formula)
    {
        select firstonly recId
            from bomVersionCheck
            where bomVersionCheck.BOMId == bomVersion.BOMId;

        if (!bomVersionCheck.RecId)
        {
            bomVersion.PmfFormulaVersionCalculation = true;
        }

        if (calcFlagChanged || (formulaSizeChanged && bomVersion.PmfFormulaVersionCalculation))
        {
            bomVersion.pmfUpdateFormulaLines(bomVersion.BOMId);
        }
    }

    inventDim = InventDim::findOrCreate(inventDimBOMVersion);
    bomVersion.InventDimId = inventDim.InventDimId;

    super();

    if (!needCheckBOM && oldRecVersion != bomVersion.RecVersion)
    {
        needCheckBOM = true;
    }

    if (inventDimBOMVersion.InventDimId != bomVersion.InventDimId)
    {
        inventDimBOMVersion.data(inventDim);
        inventDimBOMVersion_ds.setCurrent();
    }

    pmfFormCtrl_BOMTable_BOMVersion.dataSourceWritePost();

    if (   #pmfEnabled
        && bomTable.PmfBomFormula == PmfBOMFormula::Formula)
    {
        if (calcFlagChanged || (formulaSizeChanged && bomVersion.PmfFormulaVersionCalculation))
        {
            bomVersion.pmfUpdateFormulaLines(bomVersion.BOMId);
        }

        calcFlagUpdateAll = calcFlagChanged && bomVersion.PmfFormulaVersionCalculation;

        if (calcFlagUpdateAll)
        {
            bomVersion.pmfUpdateFormulaVersionCalc();
        }
    }

    ttscommit;

    if (#pmfEnabled)
    {
        element.setBOMLines();
        element.setCoByProducts();

        if (calcFlagUpdateAll)
        {
            // refresh the collection of records as the calculation flag maybe changed on one of them
            bomVersion_ds.research(true);
        }
    }
}

public boolean validateWrite()
{
    boolean ret;
    bomVersion.InventDimId = InventDim::findOrCreate(inventDimBOMVersion).InventDimId;

    ret = pmfFormCtrl_BOMTable_BOMVersion.dataSourceValidateWrite();

    if (ret)
    {
        ret = super();
    }

    return ret;
}

