    element.doRefresh(true);
    element.enableBOMTable();
    element.setBOMVersionApprove();
}

void clicked()
{
    List            selectedRecords = new List(Types::Record);
    BOMTable        bomTableSelected;

    // Get the records for each selected BOMTable
    bomTableSelected = BOMTable_ds.getFirst(true);
    if (!bomTableSelected)
    {
        bomTableSelected = BOMTable_ds.cursor();
    }

    while (bomTableSelected)
    {
        selectedRecords.addEnd(bomTableSelected);
        bomTableSelected = BOMTable_ds.getNext();
    }

    engineeringChangeAssociateCase.enabled(false);
    CaseDetailForm::runEngChgCaseAssociateForm(selectedRecords, bomTable);
    engineeringChangeAssociateCase.enabled(true);
}

