void clicked()
{
    List            selectedRecords = new List(Types::Record);
    BOM             bomSelected;
    InventTable     inventTable;

    // Get the InventTable records for each selected BOMVersion
    bomSelected = BOM_ds.getFirst(true);
    if (!bomSelected)
    {
        bomSelected = BOM_ds.cursor();
    }

    while (bomSelected)
    {
        inventTable = bomSelected.inventTable();
        selectedRecords.addEnd(inventTable);
        bomSelected = BOM_ds.getNext();
    }

    CaseDetailForm::runEngChgCaseAssociateForm(selectedRecords, inventTable);
}

void clicked()
{
    CaseDetailForm::runEngChgCaseAddToLogForm(bom.inventTable());
}

boolean leave()
{
    boolean         ret;
    ConfigGroup     configGrp;
    Dialog          dialog;
    DialogField     dialogName;
    ConfigGroupId   configGroupId = this.text();

    if (configGroupId && ! ConfigGroup::find(configGroupId))
    {
        dialog      = new Dialog("@SYS25307");
        dialogName  = dialog.addField(extendedTypeStr(Name),"@SYS6303");
        if (! dialog.run())
        {
            return false;
        }

        ttsbegin;
        configGrp.ConfigGroupId     = configGroupId;
        configGrp.Name              = dialogName.value();
        configGrp.insert();
        ttscommit;
    }

    ret = super();

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    BOM.Formula = any2enum(formulaComboBox.selection());
    BOM.update();
    pmfFormCtrl_BOMTable_BOM.fieldModified(fieldNum(BOM,Formula));
    element.enableStepConsumption(BOM);

    return ret;
}

