void billOfExchangeCreateActive()
{
    BankBillOfExchangeLayout    bankBillOfExchangeLayout;
    bankBillOfExchangeLayout = BankBillOfExchangeLayout::find(bankAccountTable.AccountID);

    if (bankBillOfExchangeLayout &&
        bankBillOfExchangeLayout.NoteNumMethod == BankBillOfExchangeNumMethod::Fixed)
    {
        bankBillOfExchangeCreate.enabled(true);
    }
    else
    {
        bankBillOfExchangeCreate.enabled(false);
    }
}

void bookRefresh()
{
    bankAccountTable_ds.refresh();
    bankAccountTable_ds.active();
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret && bankAccountTable.RecId)
    {
        //When the bank account address is created/changed, different business rules apply for that address.
        //We need to ensure the bank account is left in a good state before the form is closed.
        ret = bankAccountTable.validateWrite();
    }

    return ret;
}

void chequeCreateActive()
{
    BankChequeLayout    bankChequeLayout;
    bankChequeLayout = BankChequeLayout::find(bankAccountTable.AccountID);

    if (bankChequeLayout &&
        bankChequeLayout.ChequeNumMethod == ChequeNumMethod::Fixed)
    {
        chequeCreate.enabled(true);
    }
    else
    {
        chequeCreate.enabled(false);
    }
}

void enableActiveDates()
{
    boolean isEnabled;

    isEnabled = (bankAccountTable.BankAccountStatus == BankAccountHoldStatus::ActiveForAllTransactions);
    administration_ActiveFrom.enabled(isEnabled);
    administration_ActiveTo.enabled(isEnabled);
}

/// <summary>
/// Sets the editable property of bank statement controls.
/// </summary>
public void enableBankStatementControls()
{
    boolean isBankReconEnabled = bankAccountTable.isBankReconciliationEnabled();

    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankStatementName)).allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankStatementFormat)).allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconciliationMatchRuleSet)).allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconMatchAutoAfterImport)).allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconciliationStmtAsPaymConfirm)).allowEdit(isBankReconEnabled);
    BankReconciliation_BankStatementAsPaymConfirm.allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconAllowedPennyDifference)).allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, ReverseDebitCredit)).allowEdit(isBankReconEnabled);

    bankAccountTable_ds.object(fieldNum(BankAccountTable, TimeZonePreference)).allowEdit(isBankReconEnabled);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, TimeZone)).allowEdit(isBankReconEnabled && bankAccountTable.TimeZonePreference == DMFTimeZonePreference::Manual);
    bankAccountTable_ds.object(fieldNum(BankAccountTable, TimeZone)).visible(bankAccountTable.TimeZonePreference == DMFTimeZonePreference::Manual);

    if (FeatureStateProvider::isFeatureEnabled(BankModernReconciliationFeature::instance()))
    {
        bankAccountTable_ds.object(fieldNum(BankAccountTable, CustPaymentJournalName)).allowEdit(isBankReconEnabled);
        bankAccountTable_ds.object(fieldNum(BankAccountTable, VendPaymentJournalName)).allowEdit(isBankReconEnabled);
        bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconciliationReportFormat)).allowEdit(isBankReconEnabled);
        bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconciliationReportFormat)).visible(FeatureStateProvider::isFeatureEnabled(BankReconciliationGERReportFlight::instance()));
    }

    bankAccountTable_ds.object(fieldNum(BankAccountTable, BankReconBridgedAutoClearing)).allowEdit(isBankReconEnabled);
}

void enableButtons()
{
    this.chequeCreateActive();
    this.promissoryNoteCreateActive();
    this.billOfExchangeCreateActive();

    cheque.enabled(bankAccountTable.RecId);
    bankBillOfExchangeTable.enabled(bankAccountTable.RecId);
    bankPromissoryNoteTable.enabled(bankAccountTable.RecId);

    btnBankChequeLayout.enabled(bankAccountTable.RecId);
    btnBankBillOfExchangeLayout.enabled(bankAccountTable.RecId);
    btnBankPromissoryNoteLayout.enabled(bankAccountTable.RecId);

    if (element.validatePayrollBankAccount())
    {
        isPayrollAccount = NoYes::Yes;
        bankPositivePayGenerate.visible(false);
        payrollPositivePayGenerate.enabled(BankAccountTable.BankPositivePayFormatName != '');
        payrollPositivePayGenerate.visible(true);
    }
    else
    {
        isPayrollAccount = NoYes::No;
        payrollPositivePayGenerate.visible(false);
        bankPositivePayGenerate.enabled(bankAccountTable.BankPositivePayFormatName != '');
        bankPositivePayGenerate.visible(true);
    }

    this.setBankStatementButtonVisible();
}

