void updateDesign(boolean _init = false)
{
    if (SysCountryRegionCode::isLocationInCountryRegion(bankAccountTable.Location, [#isoFI]))
    {
        giro_FI_PrintGiro_FI.allowEdit(true);

        if (bankAccountTable.PrintGiro_FI)
        {
            giro_FI_IncludeBankBarCode_FI.allowEdit(true);
        }
        else
        {
            giro_FI_IncludeBankBarCode_FI.allowEdit(false);
        }
    }
    else
    {
        giro_FI_PrintGIro_FI.allowEdit(false);
        giro_FI_IncludeBankBarCode_FI.allowEdit(false);
    }

    BankAccountTable_DS.object(fieldNum(BankAccountTable, BankInterbankClearingCode_BE)).allowEdit(SysCountryRegionCode::isLocationInCountryRegion(bankAccountTable.Location, [#isoBE]));

    BankAccountTable_DS.object(fieldNum(BankAccountTable, BankCIN)).allowEdit(SysCountryRegionCode::isLocationInCountryRegion(bankAccountTable.Location, [#isoIT]));
    if (SysCountryRegionCode::isLocationInCountryRegion(bankAccountTable.Location, [#isoCH]))
    {
        BankAccountTable_DS.object(fieldNum(BankAccountTable, CustomerPaymentFeeLedgerDimension)).allowEdit(true);
        BankAccountTable_DS.object(fieldNum(BankAccountTable, CustPaymFeePost)).allowEdit(true);
    }
    else
    {
        BankAccountTable_DS.object(fieldNum(BankAccountTable, CustomerPaymentFeeLedgerDimension)).allowEdit(false);
        BankAccountTable_DS.object(fieldNum(BankAccountTable, CustPaymFeePost)).allowEdit(false);
    }

    // <GJP>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]))
    {
        if (Bank_JP::nameKanaValidateMasterDataNeeded())
        {
            BankAccountNameKanaValidate_JBA_JP.enabled(true);
        }
        else
        {
            BankAccountNameKanaValidate_JBA_JP.enabled(false);
        }
    }
    // </GJP>

    //Reconciliation
    element.enableBankStatementControls();

    bankAccountTable_ds.object(fieldNum(BankAccountTable, CurrencyCode)).allowEdit(bankAccountTable.allowEditCurrency());
}

public boolean validatePayrollBankAccount()
{
    PayrollParameters payrollParameters;
    VendPaymModeTable vendPaymModeTable;
    DimensionAttributeValueCombination dimAttribututeValueCombination;
    BankAccountTable bankAccountTableLocal;
    boolean isValidPayrollBankAccount = false;

    // Don't bother validating if payroll is not enabled
    if (isPayrollEnabled)
    {
        select RecId from vendPaymModeTable
                exists join RecId from dimAttribututeValueCombination
                    where dimAttribututeValueCombination.RecId == vendPaymModeTable.PaymentLedgerDimension
                exists join RecId from bankAccountTableLocal
                    where bankAccountTableLocal.AccountID == dimAttribututeValueCombination.DisplayValue
                    && bankAccountTableLocal.AccountID == BankAccountTable.AccountID
                exists join RecId from payrollParameters
                    where payrollParameters.PaymMode == vendPaymModeTable.PaymMode;

        isValidPayrollBankAccount = (vendPaymModeTable.RecId != 0);
    }

    return isValidPayrollBankAccount;
}

